// src/components/ToolBar.js
import React from 'react';
import { Link, useLocation, useNavigate } from "react-router-dom";

const TopBar = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // adjust these to match your actual routes
    const isHelpPage = location.pathname === "/help";
    return (
        <div className='topbar d-flex align-items-center justify-content-center px-2'>
            <div className='help text-white'>
                d-flex align-items-cente
            </div>
            <div className='d-flex align-items-center justify-content-end'>
                {isHelpPage ? (
                    <Link
                        className="link d-flex justify-content-center align-items-center"
                        onClick={() => navigate(-1)}
                    >
                        Back
                    </Link>
                ) : (
                    <Link
                        className="link d-flex justify-content-center align-items-center"
                        to="/help"
                    >
                        Help
                    </Link>
                )}
            </div>
        </div>
    );
};

export default TopBar;
