import React from 'react';

const ToolBar = ({ activeTool, setActiveTool }) => {
    const tools = [
        { id: "cursor", icon: "bi-cursor", flip: true },
        { id: "pencil", icon: "bi-pencil" },
        { id: "bucket", icon: "bi-paint-bucket" },
        { id: "pen", icon: "bi-vector-pen" },
        { id: "line", icon: "bi-slash-lg" },
        { id: "square", icon: "bi-square" },
        { id: "circle", icon: "bi-circle" },
        { id: "triangle", icon: "bi-triangle" },
        { id: "pentagon", icon: "bi-pentagon" },
    ];

    return (
        <ul className='draw-buttons m-0 px-2 d-flex flex-column align-items-center justify-content-center'>
            {tools.map(tool => (
                <button
                    key={tool.id}
                    className={`d-flex flex-column align-items-center ${activeTool === tool.id ? "bg-secondary text-white" : ""}`}
                    onClick={() => setActiveTool(tool.id)}
                    style={{ height: "48px" }}
                >
                    <span
                        className={`bi ${tool.icon}`}
                        style={tool.flip ? { transform: "scaleX(-1)" } : {}}
                    ></span>
                    {(tool.id === "pencil" || tool.id === "bucket") && (
                        <div className="line-color-preview"></div>
                    )}
                </button>
            ))}
        </ul>
    );
};

export default ToolBar;
