// src/pages/EditorPage.js
import React, { useState, useRef } from "react";
import Canvas from "../components/Canvas";
import ToolBar from "../components/ToolBar";

const EditorPage = () => {
    const [activeTool, setActiveTool] = useState("cursor");
    const canvasRef = useRef();

    const handleExport = () => {
        if (canvasRef.current?.exportShapes) {
            canvasRef.current.exportShapes();
        }
    };

    return (
        <div className="d-flex h-100 m-0">
            <ToolBar
                activeTool={activeTool}
                setActiveTool={setActiveTool}
                onExport={handleExport}         // pass export handler
                onLoad={handleLoad}
            />
            <div className="d-flex flex-column align-items-center justify-content-center w-100">
                <h1 className="text-white">Shape Canvas</h1>
                <Canvas ref={canvasRef} activeTool={activeTool} />
            </div>
        </div>
    );
};

export default EditorPage;
