// src/components/ToolBar.js
import React, { useRef, useEffect } from 'react';

const ToolBar = ({
    activeTool,
    setActiveTool,
    onExportJSON,
    onDownloadJPEG,
    onDownloadPDF,
    onLoad,
    onClear,
    lineColor,
    fillColor,
    setLineColor,
    setFillColor,
    strokeWidth,
    setStrokeWidth
}) => {
    // at the top of ToolBar.js (or in your icons file)
    const PolylineIcon = ({ size = 24 }) => (
        <svg
            width={size} height={size} viewBox="0 0 24 24" fill="none"
            stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"
        >
            {/* a little zig-zag polyline */}
            <polyline points="3,17 8,12 13,16 18,8 21,11" />
        </svg>
    );

    const CurveIcon = ({ size = 24 }) => (
        <svg
            width={size} height={size} viewBox="0 0 24 24" fill="none"
            stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"
        >
            {/* a smooth cubic curve from bottom-left to top-right */}
            <path d="M3,17 C8,8 16,8 21,3" />
        </svg>
    );
    const fileInputRef = useRef();

    // Refs to the dynamically created color inputs
    const fillPickerRef = useRef(null);

    // File‐load handler (unchanged)
    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (!file) return;
        const reader = new FileReader();
        reader.onload = (ev) => {
            try {
                const shapes = JSON.parse(ev.target.result);
                onLoad(shapes);
            } catch {
                console.error("Invalid JSON file");
            }
        };
        reader.readAsText(file);
        e.target.value = null;
    };

    // On mount: create hidden color inputs
    useEffect(() => {
        // — Line picker —
        const lineInput = document.createElement('input');
        lineInput.type = 'color';
        lineInput.value = lineColor;
        lineInput.style.display = 'none';
        lineInput.addEventListener('input', (e) => setLineColor(e.target.value));
        document.body.appendChild(lineInput);

        // — Fill picker —
        const fillInput = document.createElement('input');
        fillInput.type = 'color';
        fillInput.value = fillColor;
        fillInput.style.display = 'none';
        fillInput.addEventListener('input', (e) => setFillColor(e.target.value));
        document.body.appendChild(fillInput);
        fillPickerRef.current = fillInput;

        return () => {
            lineInput.remove();
            fillInput.remove();
        };
    }, [fillColor, lineColor, setFillColor, setLineColor]);

    // Update the preview bars whenever colors change
    useEffect(() => {
        const previews = document.querySelectorAll('.draw-buttons .line-color-preview');
        if (previews[0]) previews[0].style.backgroundColor = lineColor;
    }, [lineColor]);

    useEffect(() => {
        const previews = document.querySelectorAll('.draw-buttons .line-color-preview');
        if (previews[1]) previews[1].style.backgroundColor = fillColor;
    }, [fillColor]);

    // Handlers to open the native picker
    const openFillPicker = () => fillPickerRef.current && fillPickerRef.current.click();

    return (
        <ul className='draw-buttons m-0 px-2 d-flex flex-column align-items-center justify-content-center'>
            {/* Cursor */}
            <button
                onClick={() => setActiveTool("cursor")}
                className={activeTool === "cursor" ? "tool bg-black text-white" : "tool"}
            >
                <span className="bi bi-cursor" style={{ transform: "scaleX(-1)" }}></span>
            </button>

            {/* Clear */}
            <button className='tool' onClick={onClear}>
                <span className="bi bi-plus-circle"></span>
            </button>

            {/* Load JSON */}
            <button className='tool' onClick={() => fileInputRef.current.click()}>
                <span className="bi bi-upload"></span>
            </button>

            {/* stroke‐color & thickness dropdown */}
            <div className="dropdown dropend">
                <button
                    className="tool d-flex flex-column align-items-center"
                    type="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                    style={{ height: "43px", fontSize: "22px" }}
                >
                    <span className="bi bi-pencil"></span>
                    <div className="line-color-preview"></div>
                </button>
                <div className="dropdown-menu p-2 d-fex flex-column align-items-center" style={{ minWidth: '175px', backgroundColor: '#979797' }}>
                    <label className="form-label mb-1">
                        Stroke width: {strokeWidth}px
                    </label>
                    <input
                        type="range"
                        className="form-range mb-2"
                        min="1"
                        max="20"
                        value={strokeWidth}
                        onChange={e => setStrokeWidth(Number(e.target.value))}
                    />
                    <input
                        type="color"
                        className="w-100"
                        style={{borderRadius: "3px", border: "none"}}
                        value={lineColor}
                        onChange={e => setLineColor(e.target.value)}
                    />
                </div>
            </div>

            {/* Fill-color button */}
            <button
                className=" tool d-flex flex-column align-items-center"
                style={{ height: "48px" }}
                onClick={openFillPicker}
            >
                <span className="bi bi-paint-bucket"></span>
                <div className="line-color-preview"></div>
            </button>

            {/* Other tools (unchanged) */}
            <button
                onClick={() => setActiveTool("poly-line")}
                className={activeTool === "poly-line" ? "tool bg-black text-white" : "tool"}
            >
                <PolylineIcon />
            </button>
            <button
                onClick={() => setActiveTool("line")}
                className={activeTool === "line" ? "tool bg-black text-white" : "tool"}
            >
                <span className="bi bi-slash-lg"></span>
            </button>
            <button
                onClick={() => setActiveTool("curve")}
                className={activeTool === "curve" ? "tool bg-black text-white" : "tool"}
            >
                <CurveIcon />
            </button>
            <button
                onClick={() => setActiveTool("square")}
                className={activeTool === "square" ? "tool bg-black text-white" : "tool"}
            >
                <span className="bi bi-square"></span>
            </button>
            <button
                onClick={() => setActiveTool("circle")}
                className={activeTool === "circle" ? "tool bg-black text-white" : "tool"}
            >
                <span className="bi bi-circle"></span>
            </button>
            <button
                onClick={() => setActiveTool("triangle")}
                className={activeTool === "triangle" ? "tool bg-black text-white" : "tool"}
            >
                <span className="bi bi-triangle"></span>
            </button>
            <button
                onClick={() => setActiveTool("polygon")}
                className={activeTool === "polygon" ? "tool bg-black text-white" : "tool"}
            >
                <span className="bi bi-pentagon"></span>
            </button>

            {/* EXPORT DROPDOWN */}
            <div className="dropdown dropend">
                <button className='tool'
                    type="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                >
                    <span className="bi bi-download"></span>
                </button>
                <ul className="dropdown-menu" style={{backgroundColor: "#979797"}}>
                    <li>
                        <button
                            className="dropdown-item"
                            onClick={onDownloadJPEG}
                        >
                            JPEG Image (.jpg)
                        </button>
                    </li>
                    <li>
                        <button
                            className="dropdown-item"
                            onClick={onDownloadPDF}
                        >
                            PDF Document (.pdf)
                        </button>
                    </li>
                    <li>
                        <button
                            className="dropdown-item"
                            onClick={onExportJSON}
                        >
                            JSON File (.json)
                        </button>
                    </li>
                </ul>
            </div>

            {/* hidden file input (unchanged) */}
            <input
                type="file"
                accept=".json"
                ref={fileInputRef}
                style={{ display: "none" }}
                onChange={handleFileChange}
            />
        </ul>
    );
};

export default ToolBar;
