// src/components/Canvas.js
import React, {
    forwardRef, useImperativeHandle,
    useRef, useEffect, useState
} from "react";
import "../css/canvas.css";

const Canvas = forwardRef(({ activeTool, lineColor, fillColor }, ref) => {
    const c1Ref = useRef(), c2Ref = useRef();
    const shapesRef = useRef([]);
    const redoRef = useRef([]);
    const [isDrawing, setIsDrawing] = useState(false);
    const [startPos, setStartPos] = useState({ x: 0, y: 0 });

    // expose export/load
    useImperativeHandle(ref, () => ({
        exportShapes: () => {
            const data = JSON.stringify(shapesRef.current, null, 2);
            const blob = new Blob([data], { type: "application/json" });
            const url = URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url; a.download = "shapes.json"; a.click();
            URL.revokeObjectURL(url);
        },
        loadShapes: (arr) => {
            shapesRef.current = arr.map(s => ({ ...s, selected: false }));
            redrawAll();
        }
    }));

    const redrawAll = () => {
        const ctx = c1Ref.current.getContext("2d");
        ctx.clearRect(0, 0, 600, 700);
        shapesRef.current.forEach(s => {
            const { tool, x0, y0, x1, y1, selected, lineColor: lc, fillColor: fc } = s;
            ctx.save();
            ctx.strokeStyle = lc || lineColor;
            ctx.lineWidth = 1;
            ctx.fillStyle = fc || fillColor;
            ctx.beginPath();
            switch (tool) {
                case "square":
                    ctx.fillRect(x0, y0, x1 - x0, y1 - y0);
                    ctx.strokeRect(x0, y0, x1 - x0, y1 - y0);
                    break;
                case "line":
                    ctx.moveTo(x0, y0); ctx.lineTo(x1, y1); ctx.stroke();
                    break;
                case "circle":
                    const r = Math.hypot(x1 - x0, y1 - y0);
                    ctx.arc(x0, y0, r, 0, 2 * Math.PI);
                    ctx.fill(); ctx.stroke();
                    break;
                case "triangle":
                    ctx.moveTo(x0, y0);
                    ctx.lineTo(x1, y0);
                    ctx.lineTo((x0 + x1) / 2, y1);
                    ctx.closePath();
                    ctx.fill(); ctx.stroke();
                    break;
                case "polygon":
                    const sides = 5, rad = Math.hypot(x1 - x0, y1 - y0), step = 2 * Math.PI / sides;
                    for (let i = 0; i < sides; i++) {
                        const ang = i * step - Math.PI / 2;
                        const px = x0 + rad * Math.cos(ang),
                            py = y0 + rad * Math.sin(ang);
                        i === 0 ? ctx.moveTo(px, py) : ctx.lineTo(px, py);
                    }
                    ctx.closePath();
                    ctx.fill(); ctx.stroke();
                    break;
                default:
                    break;
            }
            if (selected) {
                ctx.strokeStyle = "red";
                ctx.strokeRect(Math.min(x0, x1), Math.min(y0, y1),
                    Math.abs(x1 - x0), Math.abs(y1 - y0));
            }
            ctx.restore();
        });
    };

    useEffect(() => {
        const c2 = c2Ref.current, ctx2 = c2.getContext("2d");
        const getPos = e => {
            const r = c2.getBoundingClientRect();
            return { x: e.clientX - r.left, y: e.clientY - r.top };
        };

        const down = e => {
            if (activeTool === "cursor") {
                // selection logic (unchanged)...
                const pos = getPos(e);
                shapesRef.current.forEach(s => s.selected = false);
                for (let i = shapesRef.current.length - 1; i >= 0; i--) {
                    const s = shapesRef.current[i],
                        minX = Math.min(s.x0, s.x1), maxX = Math.max(s.x0, s.x1),
                        minY = Math.min(s.y0, s.y1), maxY = Math.max(s.y0, s.y1);
                    if (pos.x >= minX && pos.x <= maxX && pos.y >= minY && pos.y <= maxY) {
                        s.selected = true; break;
                    }
                }
                redrawAll(); return;
            }
            if (!["square", "line", "circle", "triangle", "polygon"].includes(activeTool))
                return;
            setStartPos(getPos(e)); setIsDrawing(true);
        };

        const move = e => {
            if (!isDrawing) return;
            const { x, y } = getPos(e);
            const dx = x - startPos.x, dy = y - startPos.y;
            ctx2.clearRect(0, 0, 600, 700);
            ctx2.strokeStyle = lineColor;
            ctx2.beginPath();
            switch (activeTool) {
                case "square": ctx2.strokeRect(startPos.x, startPos.y, dx, dy); break;
                case "line": ctx2.moveTo(startPos.x, startPos.y); ctx2.lineTo(x, y); ctx2.stroke(); break;
                case "circle": ctx2.arc(startPos.x, startPos.y, Math.hypot(dx, dy), 0, 2 * Math.PI); ctx2.stroke(); break;
                case "triangle":
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(startPos.x + dx, startPos.y);
                    ctx2.lineTo(startPos.x + dx / 2, startPos.y + dy);
                    ctx2.closePath(); ctx2.stroke();
                    break;
                case "polygon":
                    const sides = 5, rad = Math.hypot(dx, dy), step = 2 * Math.PI / sides;
                    for (let i = 0; i < sides; i++) {
                        const ang = i * step - Math.PI / 2;
                        const px = startPos.x + rad * Math.cos(ang),
                            py = startPos.y + rad * Math.sin(ang);
                        i === 0 ? ctx2.moveTo(px, py) : ctx2.lineTo(px, py);
                    }
                    ctx2.closePath(); ctx2.stroke();
                    break;
                default:
                    break;
            }
        };

        const up = e => {
            if (!isDrawing) return;
            const { x, y } = getPos(e);
            shapesRef.current.push({
                tool: activeTool,
                x0: startPos.x, y0: startPos.y,
                x1: x, y1: y,
                lineColor, fillColor,
                selected: false
            });
            redrawAll();
            ctx2.clearRect(0, 0, 600, 700);
            setIsDrawing(false);
        };

        const onKeyDown = e => {
            if(e.ctrlKey && e.key === "z"){
                redoRef.current.push(shapesRef.current.at(shapesRef.current.length-1));
                //console.log(shapesRef.current.at(shapesRef.current.length - 1));
                //console.log(redoRef.current.at(redoRef.current.length-1));
                shapesRef.current.pop();

                redrawAll();
            }
            if (e.ctrlKey && e.shiftKey && e.key === "z") {
                shapesRef.current.push(redoRef.current.at(redoRef.current.length-1));
                console.log(redoRef.current.at(redoRef.current.length - 1));
                console.log(shapesRef.current.at(shapesRef.current.length - 1));
                redoRef.current.pop();
                redrawAll();
            }
        }

        c2.addEventListener("mousedown", down);
        c2.addEventListener("mousemove", move);
        c2.addEventListener("mouseup", up);
        window.addEventListener("keydown", onKeyDown);
        return () => {
            c2.removeEventListener("mousedown", down);
            c2.removeEventListener("mousemove", move);
            c2.removeEventListener("mouseup", up);
            window.removeEventListener("keydown", onKeyDown);

        };
    }, [activeTool, isDrawing, startPos, lineColor, fillColor]);

    return (
        <div className="canvas-container">
            <canvas ref={c1Ref} width={600} height={700} />
            <canvas ref={c2Ref} width={600} height={700} style={{ backgroundColor: "transparent" }} />
        </div>
    );
});

export default Canvas;
