import React from 'react';

const ToolBar = ({ activeTool, setActiveTool, onExport, onLoad }) => {
    const fileInputRef = useRef();

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (!file) return;
        const reader = new FileReader();
        reader.onload = (ev) => {
            try {
                const shapes = JSON.parse(ev.target.result);
                onLoad(shapes);
            } catch {
                console.error("Invalid JSON file");
            }
        };
        reader.readAsText(file);
        e.target.value = null; // reset so same file can be re-loaded
    };

    return (
        <ul className='draw-buttons m-0 px-2 d-flex flex-column align-items-center justify-content-center'>
            <button onClick={() => fileInputRef.current.click()}>
                <span className="bi bi-plus-circle"></span>
            </button>
            <button
                onClick={() => setActiveTool("cursor")}
                className={activeTool === "cursor" ? "bg-secondary text-white" : ""}
            >
                <span className="bi bi-cursor" style={{ transform: "scaleX(-1)" }}></span>
            </button>
            <button
                className="d-flex flex-column align-items-center"
                style={{ height: "43px", fontSize: "22px" }}
                onClick={() => setActiveTool("pencil")}
            >
                <span className="bi bi-pencil"></span>
                <div className="line-color-preview"></div>
            </button>
            <button
                className="d-flex flex-column align-items-center"
                style={{ height: "48px" }}
                onClick={() => setActiveTool("bucket")}
            >
                <span className="bi bi-paint-bucket"></span>
                <div className="line-color-preview"></div>
            </button>
            <button
                onClick={() => setActiveTool("pen")}
                className={activeTool === "pen" ? "bg-black text-white" : ""}
            >
                <span className="bi bi-vector-pen"></span>
            </button>
            <button
                onClick={() => setActiveTool("line")}
                className={activeTool === "line" ? "bg-black text-white" : ""}
            >
                <span className="bi bi-slash-lg"></span>
            </button>
            <button
                onClick={() => setActiveTool("square")}
                className={activeTool === "square" ? "bg-black text-white" : ""}
            >
                <span className="bi bi-square"></span>
            </button>
            <button
                onClick={() => setActiveTool("circle")}
                className={activeTool === "circle" ? "bg-black text-white" : ""}
            >
                <span className="bi bi-circle"></span>
            </button>
            <button
                onClick={() => setActiveTool("triangle")}
                className={activeTool === "triangle" ? "bg-black text-white" : ""}
            >
                <span className="bi bi-triangle"></span>
            </button>
            <button
                onClick={() => setActiveTool("polygon")}
                className={activeTool === "polygon" ? "bg-black text-white" : ""}
            >
                <span className="bi bi-pentagon"></span>
            </button>
            <button onClick={onExport}>
                <span className="bi bi-download"></span>
            </button>
            {/* hidden file input */}
            <input
                type="file"
                accept=".json"
                ref={fileInputRef}
                style={{ display: "none" }}
                onChange={handleFileChange}
            />
        </ul>
    );
};

export default ToolBar;
