// src/components/ToolBar.js
import React from 'react';
import { Link, useLocation, useNavigate } from "react-router-dom";

const TopBar = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // adjust these to match your actual routes
    const isHelpPage = location.pathname === "/help";
    const isEditorPage = location.pathname === "/editor" || location.pathname === "/";

    // If we're on HelpPage, show “Back” and go back in history;
    // otherwise (i.e. on EditorPage) show “Help” and link to /help
    return (
        <div className='topbar d-flex align-items-center justify-content-end px-2'>
            {isHelpPage ? (
                <Link
                    className="link d-flex justify-content-center align-items-center"
                    onClick={() => navigate(-1)}
                >
                    Back
                </Link>
            ) : (
                <Link
                    className="link d-flex justify-content-center align-items-center"
                    to="/help"
                >
                    Help
                </Link>
            )}
        </div>
    );
};

export default TopBar;
