// src/pages/HelpPage.js
import React, { useState, useMemo } from "react";
import TopBar from "../components/TopBar";
import { ReactComponent as PolylineIcon } from "../imgs/polyline-svgrepo-com.svg";
import { ReactComponent as CurveIcon } from "../imgs/vector-line-curve.svg";

const BUTTONS = [
    {
        id: "cursor",
        icon: <i className="bi bi-cursor" style={{ fontSize: "1.5rem", transform: "scaleX(-1)" }} />,
        name: "Cursor (Select)",
        desc: "Select, move, or resize existing shapes. Click on a shape to select it; drag to move; handles to resize."
    },
    {
        id: "clear",
        icon: <i className="bi bi-plus-circle" style={{ fontSize: "1.5rem" }} />,
        name: "New Page",
        desc: "Clears the canvas. If there are shapes present, you'll be prompted to save before wiping."
    },
    {
        id: "upload",
        icon: <i className="bi bi-upload" style={{ fontSize: "1.5rem" }} />,
        name: "Upload (Load JSON)",
        desc: "Opens your file picker to load a previously exported JSON file of shapes onto the canvas."
    },
    {
        id: "stroke",
        icon: <i className="bi bi-pencil" style={{ fontSize: "1.5rem" }} />,
        name: "Line Color & Thickness",
        desc: "Dropdown: use the slider to set stroke width, and the color picker to choose your line color."
    },
    {
        id: "fill",
        icon: <i className="bi bi-paint-bucket" style={{ fontSize: "1.5rem" }} />,
        name: "Fill Color",
        desc: "Opens a native color picker for setting the fill color of shapes before you draw them."
    },
    {
        id: "line",
        icon: <i className="bi bi-slash-lg" style={{ fontSize: "1.5rem" }} />,
        name: "Line",
        desc: "Draw single straight segments. Click to start, drag to end, release to finish each line."
    },
    {
        id: "poly-line",
        icon: <PolylineIcon width={24} height={24} />,
        name: "Poly-Line",
        desc: "Click to place each vertex. Double-click or press Enter to close out the multi-segment line."
    },
    {
        id: "curve",
        icon: <CurveIcon width={24} height={24} />,
        name: "Curve",
        desc: "Click-and-drag to draw Bezier curves. Drag the control handles to tweak the curve's shape."
    },
    {
        id: "square",
        icon: <i className="bi bi-square" style={{ fontSize: "1.5rem" }} />,
        name: "Rectangle / Square",
        desc: "Drag to create a rectangle. Hold Shift while dragging to constrain proportions to a square."
    },
    {
        id: "circle",
        icon: <i className="bi bi-circle" style={{ fontSize: "1.5rem" }} />,
        name: "Ellipse / Circle",
        desc: "Drag to create an ellipse. Hold Shift to make a perfect circle."
    },
    {
        id: "triangle",
        icon: <i className="bi bi-triangle" style={{ fontSize: "1.5rem" }} />,
        name: "Triangle",
        desc: "Drag to set the base and height of an isosceles triangle."
    },
    {
        id: "polygon",
        icon: <i className="bi bi-pentagon" style={{ fontSize: "1.5rem" }} />,
        name: "Polygon",
        desc: "Click to drop each vertex. Double-click or press Enter when you're done to close the shape."
    },
    {
        id: "export",
        icon: <i className="bi bi-download" style={{ fontSize: "1.5rem" }} />,
        name: "Export / Download",
        desc: `Dropdown with:
 • JPEG (.jpg) – download canvas as an image
 • PDF (.pdf) – download canvas as a PDF
 • JSON (.json) – download the raw shapes data.`
    },
];

const HelpPage = () => {
    const [search, setSearch] = useState("");

    // only recompute filter when search changes
    const filtered = useMemo(() => {
        const term = search.toLowerCase();
        return BUTTONS.filter(
            ({ name, desc }) =>
                name.toLowerCase().includes(term) ||
                desc.toLowerCase().includes(term)
        );
    }, [search]);

    return (
        <div className="h-100 m-0" style={{ overflowY: "auto" }}>
            <TopBar />
            <div className="p-3">
                <input
                    type="text"
                    className="form-control mb-3"
                    placeholder="Search buttons…"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />

                {filtered.map((btn) => (
                    <div
                        key={btn.id}
                        className="d-flex align-items-start bg-white rounded p-3 mb-2 shadow-sm"
                    >
                        <div className="me-3">{btn.icon}</div>
                        <div>
                            <h5 className="mb-1">{btn.name}</h5>
                            <p className="mb-0" style={{ fontSize: "0.9rem" }}>
                                {btn.desc}
                            </p>
                        </div>
                    </div>
                ))}

                {filtered.length === 0 && (
                    <p className="text-muted">No buttons match your search.</p>
                )}
            </div>
        </div>
    );
};

export default HelpPage;
