// src/components/Canvas.js
import React, {
    useRef,
    useEffect,
    useState,
    forwardRef,
    useImperativeHandle,
} from "react";
import "../css/canvas.css";

const Canvas = forwardRef(({ activeTool }, ref) => {
    const canvas1Ref = useRef(null);
    const canvas2Ref = useRef(null);
    const shapesRef = useRef([]);
    const [isDrawing, setIsDrawing] = useState(false);
    const [startPos, setStartPos] = useState({ x: 0, y: 0 });

    // expose exportShapes to parent
    useImperativeHandle(ref, () => ({
        exportShapes: () => {
            const dataStr = JSON.stringify(shapesRef.current, null, 2);
            const blob = new Blob([dataStr], { type: "application/json" });
            const url = URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url;
            a.download = "shapes.json";
            a.click();
            URL.revokeObjectURL(url);
        },
        loadShapes: (loadedShapes) => {
            //reset and redraw
            shapesRef.current = loadedShapes.map(s => ({...s, selected: false}))
            redrawAll();
        }
    }));

    const redrawAll = () => {
        const ctx = canvas1Ref.current.getContext("2d");
        ctx.clearRect(0, 0, 600, 700);
        shapesRef.current.forEach((s) => {
            ctx.beginPath();
            ctx.strokeStyle = s.selected ? "red" : "#000";
            const { tool, x0, y0, x1, y1 } = s;
            switch (tool) {
                case "square":
                    ctx.strokeRect(x0, y0, x1 - x0, y1 - y0);
                    break;
                case "line":
                    ctx.moveTo(x0, y0);
                    ctx.lineTo(x1, y1);
                    ctx.stroke();
                    break;
                case "circle":
                    ctx.arc(x0, y0, Math.hypot(x1 - x0, y1 - y0), 0, 2 * Math.PI);
                    ctx.stroke();
                    break;
                case "triangle":
                    ctx.moveTo(x0, y0);
                    ctx.lineTo(x1, y0);
                    ctx.lineTo((x0 + x1) / 2, y1);
                    ctx.closePath();
                    ctx.stroke();
                    break;
                case "polygon":
                    const sides = 5,
                        radius = Math.hypot(x1 - x0, y1 - y0),
                        step = (2 * Math.PI) / sides;
                    for (let i = 0; i < sides; i++) {
                        const ang = i * step - Math.PI / 2;
                        const px = x0 + radius * Math.cos(ang),
                            py = y0 + radius * Math.sin(ang);
                        i === 0 ? ctx.moveTo(px, py) : ctx.lineTo(px, py);
                    }
                    ctx.closePath();
                    ctx.stroke();
                    break;
                default:
                    break;
            }
        });
    };

    useEffect(() => {
        const c2 = canvas2Ref.current;
        const ctx2 = c2.getContext("2d");

        const getCoords = (e) => {
            const r = c2.getBoundingClientRect();
            return { x: e.clientX - r.left, y: e.clientY - r.top };
        };

        const onMouseDown = (e) => {
            if (activeTool === "cursor") {
                // ... selection logic (unchanged) ...
                return;
            }
            if (!["square", "line", "circle", "triangle", "polygon"].includes(activeTool)) return;
            setStartPos(getCoords(e));
            setIsDrawing(true);
        };
        const onMouseMove = (e) => {
            if (!isDrawing) return;
            const { x, y } = getCoords(e);
            const dx = x - startPos.x,
                dy = y - startPos.y;
            ctx2.clearRect(0, 0, 600, 700);
            ctx2.beginPath();
            switch (activeTool) {
                case "square":
                    ctx2.strokeRect(startPos.x, startPos.y, dx, dy);
                    break;
                case "line":
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(x, y);
                    ctx2.stroke();
                    break;
                case "circle":
                    ctx2.arc(startPos.x, startPos.y, Math.hypot(dx, dy), 0, 2 * Math.PI);
                    ctx2.stroke();
                    break;
                case "triangle":
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(startPos.x + dx, startPos.y);
                    ctx2.lineTo(startPos.x + dx / 2, startPos.y + dy);
                    ctx2.closePath();
                    ctx2.stroke();
                    break;
                case "polygon":
                    const sides = 5, radius = Math.hypot(dx, dy), step = (2 * Math.PI) / sides;
                    for (let i = 0; i < sides; i++) {
                        const ang = i * step - Math.PI / 2;
                        const px = startPos.x + radius * Math.cos(ang),
                            py = startPos.y + radius * Math.sin(ang);
                        i === 0 ? ctx2.moveTo(px, py) : ctx2.lineTo(px, py);
                    }
                    ctx2.closePath();
                    ctx2.stroke();
                    break;
                default:
                    break;
            }
        };
        const onMouseUp = (e) => {
            if (!isDrawing) return;
            const { x, y } = getCoords(e);
            shapesRef.current.push({
                tool: activeTool,
                x0: startPos.x, y0: startPos.y,
                x1: x, y1: y,
                selected: false
            });
            redrawAll();
            ctx2.clearRect(0, 0, 600, 700);
            setIsDrawing(false);
        };

        const onKeyDown = e => {
            // Undo: Ctrl+Z
            if (e.ctrlKey && e.key === "z") {
                shapesRef.current.pop();
                redrawAll();
            }
        };

        c2.addEventListener("mousedown", onMouseDown);
        c2.addEventListener("mousemove", onMouseMove);
        c2.addEventListener("mouseup", onMouseUp);
        window.addEventListener("keydown", onKeyDown);
        return () => {
            c2.removeEventListener("mousedown", onMouseDown);
            c2.removeEventListener("mousemove", onMouseMove);
            c2.removeEventListener("mouseup", onMouseUp);
            window.removeEventListener("keydown", onKeyDown)
        };
    }, [activeTool, isDrawing, startPos]);

    return (
        <div className="canvas-container">
            <canvas ref={canvas1Ref} width={600} height={700} />
            <canvas ref={canvas2Ref} width={600} height={700} style={{ backgroundColor: "transparent" }} />
        </div>
    );
});

export default Canvas;
