// src/pages/EditorPage.js
import React, { useState, useRef } from "react";
import Canvas from "../components/Canvas";
import ToolBar from "../components/ToolBar";
import "../css/editor.css";

const EditorPage = () => {
    const [activeTool, setActiveTool] = useState("cursor");
    const [lineColor, setLineColor] = useState("#000000");
    const [fillColor, setFillColor] = useState("#ffffff");
    const canvasRef = useRef();

    const handleExportJSON = () => canvasRef.current?.exportShapes();
    const handleDownloadJPEG = () => canvasRef.current?.downloadJPEG();
    const handleDownloadPDF = () => canvasRef.current?.downloadPDF();
    const handleLoad = (shapes) => canvasRef.current?.loadShapes(shapes);

    return (
        <div className="editor-container d-flex">
            {/* Toolbar stays its natural width */}
            <ToolBar
                activeTool={activeTool}
                setActiveTool={setActiveTool}
                onExportJSON={handleExportJSON}
                onDownloadJPEG={handleDownloadJPEG}
                onDownloadPDF={handleDownloadPDF}
                onLoad={handleLoad}
                lineColor={lineColor}
                fillColor={fillColor}
                setLineColor={setLineColor}
                setFillColor={setFillColor}
            />

            {/* This wrapper will flex to fill the rest of the page */}
            <div className="canvas-wrapper">
                <Canvas
                    ref={canvasRef}
                    activeTool={activeTool}
                    lineColor={lineColor}
                    fillColor={fillColor}
                />
            </div>
        </div>
    );
};

export default EditorPage;
