// src/components/Canvas.js
import React, { useRef, useEffect, useState } from "react";
import "../css/canvas.css";

const Canvas = ({ activeTool }) => {
    const canvas1Ref = useRef(null);
    const canvas2Ref = useRef(null);

    const [isDrawing, setIsDrawing] = useState(false);
    const [startPos, setStartPos] = useState({ x: 0, y: 0 });

    useEffect(() => {
        const canvas1 = canvas1Ref.current;
        const canvas2 = canvas2Ref.current;
        const ctx1 = canvas1.getContext("2d");
        const ctx2 = canvas2.getContext("2d");

        const getCoords = (e) => {
            const rect = canvas2.getBoundingClientRect();
            return { x: e.clientX - rect.left, y: e.clientY - rect.top };
        };

        const handleMouseDown = (e) => {
            // only start drawing on one of our shape tools
            if (!["square", "line", "circle", "triangle", "polygon"].includes(activeTool)) {
                return;
            }
            const pos = getCoords(e);
            setStartPos(pos);
            setIsDrawing(true);
        };

        const drawPreview = ({ x, y }) => {
            const dx = x - startPos.x;
            const dy = y - startPos.y;
            ctx2.clearRect(0, 0, canvas2.width, canvas2.height);

            ctx2.strokeStyle = "#000";
            switch (activeTool) {
                case "square":
                    ctx2.strokeRect(startPos.x, startPos.y, dx, dy);
                    break;

                case "line":
                    ctx2.beginPath();
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(x, y);
                    ctx2.stroke();
                    break;

                case "circle":
                    ctx2.beginPath();
                    // radius = distance from start to current
                    const r = Math.hypot(dx, dy);
                    ctx2.arc(startPos.x, startPos.y, r, 0, 2 * Math.PI);
                    ctx2.stroke();
                    break;

                case "triangle":
                    ctx2.beginPath();
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(startPos.x + dx, startPos.y);
                    ctx2.lineTo(startPos.x + dx / 2, startPos.y + dy);
                    ctx2.closePath();
                    ctx2.stroke();
                    break;

                case "polygon":
                    const sides = 5;                       // pentagon
                    const radiusPoly = Math.hypot(dx, dy);
                    const step = (2 * Math.PI) / sides;
                    ctx2.beginPath();
                    for (let i = 0; i < sides; i++) {
                        const ang = i * step - Math.PI / 2;  // start at top
                        const px = startPos.x + radiusPoly * Math.cos(ang);
                        const py = startPos.y + radiusPoly * Math.sin(ang);
                        i === 0 ? ctx2.moveTo(px, py) : ctx2.lineTo(px, py);
                    }
                    ctx2.closePath();
                    ctx2.stroke();
                    break;
            }
        };

        const handleMouseMove = (e) => {
            if (!isDrawing) return;
            const pos = getCoords(e);
            drawPreview(pos);
        };

        const handleMouseUp = (e) => {
            if (!isDrawing) return;
            const pos = getCoords(e);
            const dx = pos.x - startPos.x;
            const dy = pos.y - startPos.y;

            // draw the final shape onto layer1
            ctx1.strokeStyle = "#000";
            switch (activeTool) {
                case "square":
                    ctx1.strokeRect(startPos.x, startPos.y, dx, dy);
                    break;

                case "line":
                    ctx1.beginPath();
                    ctx1.moveTo(startPos.x, startPos.y);
                    ctx1.lineTo(pos.x, pos.y);
                    ctx1.stroke();
                    break;

                case "circle":
                    ctx1.beginPath();
                    const r = Math.hypot(dx, dy);
                    ctx1.arc(startPos.x, startPos.y, r, 0, 2 * Math.PI);
                    ctx1.stroke();
                    break;

                case "triangle":
                    ctx1.beginPath();
                    ctx1.moveTo(startPos.x, startPos.y);
                    ctx1.lineTo(startPos.x + dx, startPos.y);
                    ctx1.lineTo(startPos.x + dx / 2, startPos.y + dy);
                    ctx1.closePath();
                    ctx1.stroke();
                    break;

                case "polygon":
                    const sides = 5;
                    const radiusPoly = Math.hypot(dx, dy);
                    const step = (2 * Math.PI) / sides;
                    ctx1.beginPath();
                    for (let i = 0; i < sides; i++) {
                        const ang = i * step - Math.PI / 2;
                        const px = startPos.x + radiusPoly * Math.cos(ang);
                        const py = startPos.y + radiusPoly * Math.sin(ang);
                        i === 0 ? ctx1.moveTo(px, py) : ctx1.lineTo(px, py);
                    }
                    ctx1.closePath();
                    ctx1.stroke();
                    break;
            }

            // clear preview and reset
            ctx2.clearRect(0, 0, canvas2.width, canvas2.height);
            setIsDrawing(false);
        };

        canvas2.addEventListener("mousedown", handleMouseDown);
        canvas2.addEventListener("mousemove", handleMouseMove);
        canvas2.addEventListener("mouseup", handleMouseUp);

        return () => {
            canvas2.removeEventListener("mousedown", handleMouseDown);
            canvas2.removeEventListener("mousemove", handleMouseMove);
            canvas2.removeEventListener("mouseup", handleMouseUp);
        };
    }, [activeTool, isDrawing, startPos]);

    return (
        <div className="canvas-container">
            <canvas ref={canvas1Ref} width="600" height="700" />
            <canvas ref={canvas2Ref} width="600" height="700" style={{backgroundColor: "transparent"}}/>
        </div>
    );
};

export default Canvas;
