// src/components/Canvas.js
import React, {
    forwardRef,
    useImperativeHandle,
    useRef,
    useEffect,
    useState
} from "react";
import { jsPDF } from "jspdf";
import "../css/canvas.css";

const Canvas = forwardRef(({ activeTool, lineColor, fillColor }, ref) => {
    const containerRef = useRef(null);
    const c1Ref = useRef(null);
    const c2Ref = useRef(null);
    const [size, setSize] = useState({ width: 0, height: 0 });
    const shapesRef = useRef([]);
    const redoRef = useRef([]);
    const [isDrawing, setIsDrawing] = useState(false);
    const [startPos, setStartPos] = useState({ x: 0, y: 0 });

    useImperativeHandle(ref, () => ({
        exportShapes: () => {
            const data = JSON.stringify(shapesRef.current, null, 2);
            const blob = new Blob([data], { type: "application/json" });
            const url = URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url;
            a.download = "shapes.json";
            a.click();
            URL.revokeObjectURL(url);
        },
        downloadJPEG: () => {
            const tmp = document.createElement("canvas");
            tmp.width = size.width;
            tmp.height = size.height;
            const tctx = tmp.getContext("2d");
            tctx.fillStyle = "#979797";
            tctx.fillRect(0, 0, tmp.width, tmp.height);
            tctx.drawImage(c1Ref.current, 0, 0);
            const dataURL = tmp.toDataURL("image/jpeg", 1.0);
            const link = document.createElement("a");
            link.href = dataURL;
            link.download = "canvas.jpg";
            link.click();
        },
        downloadPDF: () => {
            const tmp = document.createElement("canvas");
            tmp.width = size.width;
            tmp.height = size.height;
            const tctx = tmp.getContext("2d");
            tctx.fillStyle = "#979797";
            tctx.fillRect(0, 0, tmp.width, tmp.height);
            tctx.drawImage(c1Ref.current, 0, 0);
            const imgData = tmp.toDataURL("image/jpeg", 1.0);
            const pdf = new jsPDF({ unit: "px", format: [size.width, size.height] });
            pdf.addImage(imgData, "JPEG", 0, 0, size.width, size.height);
            pdf.save("canvas.pdf");
        },
        loadShapes: (arr) => {
            shapesRef.current = arr.map(s => ({ ...s, selected: false }));
            redrawAll();
        }
    }));

    const redrawAll = () => {
        const ctx = c1Ref.current.getContext("2d");
        ctx.clearRect(0, 0, size.width, size.height);
        ctx.fillStyle = "#979797";
        ctx.fillRect(0, 0, size.width, size.height);
        shapesRef.current.forEach(s => {
            const { tool, x0, y0, x1, y1, lineColor: lc, fillColor: fc, selected } = s;
            ctx.save();
            ctx.strokeStyle = lc || lineColor;
            ctx.fillStyle = fc || fillColor;
            ctx.lineWidth = 1;
            ctx.beginPath();
            switch (tool) {
                case "square":
                    ctx.fillRect(x0, y0, x1 - x0, y1 - y0);
                    ctx.strokeRect(x0, y0, x1 - x0, y1 - y0);
                    break;
                case "line":
                    ctx.moveTo(x0, y0);
                    ctx.lineTo(x1, y1);
                    ctx.stroke();
                    break;
                case "circle":
                    const r = Math.hypot(x1 - x0, y1 - y0);
                    ctx.arc(x0, y0, r, 0, 2 * Math.PI);
                    ctx.fill();
                    ctx.stroke();
                    break;
                case "triangle":
                    ctx.moveTo(x0, y0);
                    ctx.lineTo(x1, y0);
                    ctx.lineTo((x0 + x1) / 2, y1);
                    ctx.closePath();
                    ctx.fill();
                    ctx.stroke();
                    break;
                case "polygon":
                    const sides = 5;
                    const rad = Math.hypot(x1 - x0, y1 - y0);
                    const step = 2 * Math.PI / sides;
                    for (let i = 0; i < sides; i++) {
                        const ang = i * step - Math.PI / 2;
                        const px = x0 + rad * Math.cos(ang);
                        const py = y0 + rad * Math.sin(ang);
                        i === 0 ? ctx.moveTo(px, py) : ctx.lineTo(px, py);
                    }
                    ctx.closePath();
                    ctx.fill();
                    ctx.stroke();
                    break;
            }
            if (selected) {
                ctx.strokeStyle = "red";
                ctx.strokeRect(
                    Math.min(x0, x1),
                    Math.min(y0, y1),
                    Math.abs(x1 - x0),
                    Math.abs(y1 - y0)
                );
            }
            ctx.restore();
        });
    };

    useEffect(() => {
        const resize = () => {
            const { width, height } = containerRef.current.getBoundingClientRect();
            setSize({ width, height });
        };
        resize();
        window.addEventListener("resize", resize);
        return () => window.removeEventListener("resize", resize);
    }, []);

    useEffect(() => {
        c1Ref.current.width = size.width;
        c1Ref.current.height = size.height;
        c2Ref.current.width = size.width;
        c2Ref.current.height = size.height;
        redrawAll();
    }, [size]);

    useEffect(() => {
        const c2 = c2Ref.current;
        const ctx2 = c2.getContext("2d");

        const getPos = (e) => {
            const r = c2.getBoundingClientRect();
            return { x: e.clientX - r.left, y: e.clientY - r.top };
        };

        const down = (e) => {
            if (activeTool === "cursor") {
                const pos = getPos(e);
                shapesRef.current.forEach(s => s.selected = false);
                for (let i = shapesRef.current.length - 1; i >= 0; i--) {
                    const s = shapesRef.current[i];
                    const minX = Math.min(s.x0, s.x1);
                    const maxX = Math.max(s.x0, s.x1);
                    const minY = Math.min(s.y0, s.y1);
                    const maxY = Math.max(s.y0, s.y1);
                    if (pos.x >= minX && pos.x <= maxX && pos.y >= minY && pos.y <= maxY) {
                        s.selected = true;
                        break;
                    }
                }
                redrawAll();
                return;
            }
            if (!["square", "line", "circle", "triangle", "polygon"].includes(activeTool)) {
                return;
            }
            const pos = getPos(e);
            setStartPos(pos);
            setIsDrawing(true);
        };

        const move = (e) => {
            if (!isDrawing) return;
            const { x, y } = getPos(e);
            const dx = x - startPos.x;
            const dy = y - startPos.y;
            ctx2.clearRect(0, 0, size.width, size.height);
            ctx2.strokeStyle = lineColor;
            ctx2.beginPath();
            switch (activeTool) {
                case "square":
                    ctx2.strokeRect(startPos.x, startPos.y, dx, dy);
                    break;
                case "line":
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(x, y);
                    ctx2.stroke();
                    break;
                case "circle":
                    ctx2.arc(startPos.x, startPos.y, Math.hypot(dx, dy), 0, 2 * Math.PI);
                    ctx2.stroke();
                    break;
                case "triangle":
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(startPos.x + dx, startPos.y);
                    ctx2.lineTo(startPos.x + dx / 2, startPos.y + dy);
                    ctx2.closePath();
                    ctx2.stroke();
                    break;
                case "polygon":
                    const sides2 = 5;
                    const rad2 = Math.hypot(dx, dy);
                    const step2 = 2 * Math.PI / sides2;
                    for (let i = 0; i < sides2; i++) {
                        const ang = i * step2 - Math.PI / 2;
                        const px = startPos.x + rad2 * Math.cos(ang);
                        const py = startPos.y + rad2 * Math.sin(ang);
                        i === 0 ? ctx2.moveTo(px, py) : ctx2.lineTo(px, py);
                    }
                    ctx2.closePath();
                    ctx2.stroke();
                    break;
            }
        };

        const up = (e) => {
            if (!isDrawing) return;
            const { x, y } = getPos(e);
            shapesRef.current.push({
                tool: activeTool,
                x0: startPos.x,
                y0: startPos.y,
                x1: x,
                y1: y,
                lineColor,
                fillColor,
                selected: false
            });
            redrawAll();
            ctx2.clearRect(0, 0, size.width, size.height);
            setIsDrawing(false);
        };

        const onKeyDown = (e) => {
            if (e.ctrlKey && e.key === "z" && shapesRef.current.length) {
                redoRef.current.push(shapesRef.current.pop());
                redrawAll();
            } else if (e.ctrlKey && e.shiftKey && e.key.toLowerCase() === "z" && redoRef.current.length) {
                shapesRef.current.push(redoRef.current.pop());
                redrawAll();
            }
        };

        c2.addEventListener("mousedown", down);
        c2.addEventListener("mousemove", move);
        c2.addEventListener("mouseup", up);
        window.addEventListener("keydown", onKeyDown);
        return () => {
            c2.removeEventListener("mousedown", down);
            c2.removeEventListener("mousemove", move);
            c2.removeEventListener("mouseup", up);
            window.removeEventListener("keydown", onKeyDown);
        };
    }, [activeTool, isDrawing, startPos, lineColor, fillColor, size]);

    return (
        <div ref={containerRef} className="canvas-container">
            <canvas ref={c1Ref} style={{ position: "absolute", left: 0, top: 0 }} />
            <canvas ref={c2Ref} style={{ position: "absolute", left: 0, top: 0 }} />
        </div>
    );
});

export default Canvas;
