import '../notes.css';
import React from 'react';
import download from '../imgs/download.png';
import add from '../imgs/add.png';
import undo from '../imgs/undo.png';
import redo from '../imgs/redo.png';
import left from '../imgs/left.png';
import center from '../imgs/center.png';
import right from '../imgs/right.png';
import bullet from '../imgs/bullet.png';
import list from '../imgs/list.png';
import plus from '../imgs/plus.png';
import minus from '../imgs/minus.png';

class EditBar extends React.Component {
    render() {
        return (
            <div class="format-bar">
                <ul class="format-buttons">
                    <button><i class="bi bi-plus-circle"></i></button>
                    <button><img src={download} /></button>
                    <button><img src={undo}/></button>
                    <button><img src={redo} /></button>
                    <input type='text' placeholder='Times New...'></input>
                    <button><b>B</b></button>
                    <button><i>I</i></button>
                    <button><u>U</u></button>
                    <button>A</button>
                    <div id="font_size">
                        <button><img src={minus}/></button>
                        <input type="numeric" value="10" ></input>
                        <button><img src={plus}/></button>
                    </div>
                    <button><img src={left}/></button>
                    <button><img src={center} /></button>
                    <button><img src={right}/></button>
                    <button><img src={bullet} /></button>
                    <button><img src={list}/></button>
                </ul>
            </div>
        );
    }
}

export default EditBar;
