// src/components/Canvas.js
import React, {
    forwardRef,
    useImperativeHandle,
    useRef,
    useEffect,
    useState
} from "react";
import { jsPDF } from "jspdf";
import "../css/canvas.css";

const Canvas = forwardRef(({ activeTool, lineColor, fillColor, strokeWidth }, ref) => {
    const containerRef = useRef(null);
    const c1Ref = useRef(null);
    const c2Ref = useRef(null);

    const shapesRef = useRef([]);
    const redoRef = useRef([]);

    const polyRef = useRef([]);
    const curveRef = useRef([]);

    const [isDrawing, setIsDrawing] = useState(false);
    const [startPos, setStartPos] = useState({ x: 0, y: 0 });

    // 1) Expose export/load/download handlers
    useImperativeHandle(ref, () => ({
        exportShapes: () => {
            const data = JSON.stringify(shapesRef.current, null, 2);
            const blob = new Blob([data], { type: "application/json" });
            const url = URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url; a.download = "shapes.json"; a.click();
            URL.revokeObjectURL(url);
        },
        downloadJPEG: () => {
            const shapes = shapesRef.current;
            const pad = 20;

            // 1) Compute the tight bounds for all shapes (including circles/polygons)
            let minX = Infinity, minY = Infinity;
            let maxX = -Infinity, maxY = -Infinity;

            shapes.forEach(s => {
                if (s.tool === "circle" || s.tool === "polygon") {
                    const r = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                    minX = Math.min(minX, s.x0 - r);
                    maxX = Math.max(maxX, s.x0 + r);
                    minY = Math.min(minY, s.y0 - r);
                    maxY = Math.max(maxY, s.y0 + r);
                } else {
                    minX = Math.min(minX, s.x0, s.x1);
                    maxX = Math.max(maxX, s.x0, s.x1);
                    minY = Math.min(minY, s.y0, s.y1);
                    maxY = Math.max(maxY, s.y0, s.y1);
                }
            });

            // If no shapes, default to entire canvas
            if (minX === Infinity) {
                minX = 0; minY = 0;
                maxX = c1Ref.current.width;
                maxY = c1Ref.current.height;
            }

            // 2) Add padding and clamp to canvas size
            minX = minX - pad;
            minY = minY - pad;
            maxX = maxX + pad;
            maxY = maxY + pad;

            const sw = maxX - minX;
            const sh = maxY - minY;

            // 3) Create offscreen canvas exactly sw×sh
            const tmp = document.createElement("canvas");
            tmp.width = sw;
            tmp.height = sh;

            // 4) Replay your redrawAll INTO that offscreen canvas,
            //    offsetting every shape by (-minX, -minY)
            redrawAll({ current: tmp }, -minX, -minY, "#ffffff");

            // 5) Export the offscreen canvas as JPEG
            const dataURL = tmp.toDataURL("image/jpeg", 1.0);
            const link = document.createElement("a");
            link.href = dataURL;
            link.download = "shapes.jpg";
            link.click();
        },
        downloadPDF: () => {
            const shapes = shapesRef.current;
            const pad = 20;

            // 1) compute tight bounds (incl. full circles/polygons)
            let minX = Infinity, minY = Infinity;
            let maxX = -Infinity, maxY = -Infinity;

            shapes.forEach(s => {
                if (s.tool === "circle" || s.tool === "polygon") {
                    const r = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                    minX = Math.min(minX, s.x0 - r);
                    maxX = Math.max(maxX, s.x0 + r);
                    minY = Math.min(minY, s.y0 - r);
                    maxY = Math.max(maxY, s.y0 + r);
                } else {
                    minX = Math.min(minX, s.x0, s.x1);
                    maxX = Math.max(maxX, s.x0, s.x1);
                    minY = Math.min(minY, s.y0, s.y1);
                    maxY = Math.max(maxY, s.y0, s.y1);
                }
            });

            // fallback to full canvas if no shapes
            if (minX === Infinity) {
                minX = 0; minY = 0;
                maxX = c1Ref.current.width;
                maxY = c1Ref.current.height;
            }

            // 2) apply padding (no clamping)
            minX -= pad;
            minY -= pad;
            maxX += pad;
            maxY += pad;

            const sw = maxX - minX;
            const sh = maxY - minY;

            // 3) offscreen canvas exactly sw×sh
            const tmp = document.createElement("canvas");
            tmp.width = sw;
            tmp.height = sh;

            // replay draw
            redrawAll({ current: tmp }, -minX, -minY, "#ffffff");

            // 4) setup jsPDF
            const orientation = sw > sh ? "landscape" : "portrait";
            const pdf = new jsPDF({ orientation, unit: "px", format: "a4" });
            const pageW = pdf.internal.pageSize.getWidth();
            const pageH = pdf.internal.pageSize.getHeight();

            // 5) compute scale to fit
            const scale = Math.min(pageW / sw, pageH / sh);

            // 6) split vertically into pages if needed
            let yOffset = 0;
            while (yOffset < sh) {
                // slice height in source pixels
                const sliceH = Math.min(sh - yOffset, pageH / scale);

                // draw slice to a tiny canvas
                const slice = document.createElement("canvas");
                slice.width = sw;
                slice.height = sliceH;
                slice.getContext("2d").drawImage(
                    tmp,
                    0, yOffset, sw, sliceH,
                    0, 0, sw, sliceH
                );

                // add slice image to PDF
                const imgData = slice.toDataURL("image/jpeg", 1.0);
                const imgW = sw * scale, imgH = sliceH * scale;
                const xPos = (pageW - imgW) / 2;
                pdf.addImage(imgData, "JPEG", xPos, 0, imgW, imgH);

                yOffset += sliceH;
                if (yOffset < sh) pdf.addPage();
            }

            // 7) save
            pdf.save("shapes.pdf");
        },
        loadShapes: (arr) => {
            shapesRef.current = arr.map(s => ({ ...s, selected: false }));
            redrawAll();
        },

        getShapeCount: () => shapesRef.current.length,
        clearShapes: () => {
            shapesRef.current = [];
            redrawAll();
        }

    }));

    // 2) Resize loop: keep canvas internal size in sync with CSS size
    useEffect(() => {
        const resize = () => {
            const { width, height } = containerRef.current.getBoundingClientRect();
            c1Ref.current.width = width;
            c1Ref.current.height = height;
            c2Ref.current.width = width;
            c2Ref.current.height = height;
            redrawAll();
        };
        resize();
        window.addEventListener("resize", resize);
        return () => window.removeEventListener("resize", resize);
    }, []);

    // 3) Redraw everything (with baked-in background)
    function redrawAll(context = c1Ref, offsetX = 0, offsetY = 0, backgroundColor = "#979797") {
        const c = context.current;
        const ctx = c.getContext("2d")
        const w = ctx.canvas.width;
        const h = ctx.canvas.height;

        // 1) clear & paint background
        ctx.clearRect(0, 0, w, h);
        ctx.fillStyle = backgroundColor;
        ctx.fillRect(0, 0, w, h);

        // 2) draw each shape, offset by offsetX/offsetY
        shapesRef.current.forEach(s => {
            const lc = s.lineColor || lineColor;
            const fc = s.fillColor || fillColor;
            const sw = s.strokeWidth || strokeWidth;
            ctx.save();
            ctx.strokeStyle = lc;
            ctx.fillStyle = fc;
            ctx.lineWidth = sw;
            ctx.beginPath();

            const x0 = s.x0 + offsetX;
            const y0 = s.y0 + offsetY;
            const x1 = s.x1 + offsetX;
            const y1 = s.y1 + offsetY;

            switch (s.tool) {
                case "square":
                    // compute raw delta
                    const dx = s.x1 - s.x0;
                    const dy = s.y1 - s.y0;

                    let w, h;
                    if (s.isSquare) {
                        // lock into a square: choose the smaller side
                        const side = Math.min(Math.abs(dx), Math.abs(dy));
                        w = dx < 0 ? -side : side;
                        h = dy < 0 ? -side : side;
                    } else {
                        // free‐form rectangle
                        w = dx;
                        h = dy;
                    }

                    ctx.fillRect(s.x0 + offsetX, s.y0 + offsetY, w, h);
                    ctx.strokeRect(s.x0 + offsetX, s.y0 + offsetY, w, h);
                    break;
                case "line":
                    ctx.moveTo(x0, y0);
                    ctx.lineTo(x1, y1);
                    ctx.stroke();
                    break;

                case "circle":
                    console.log("final circle draw; isCircle=", s.isCircle);
                    if (s.isCircle) {
                        // perfect circle
                        const r = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                        ctx.arc(x0, y0, r, 0, 2 * Math.PI);
                    } else {
                        // free‐form ellipse
                        const rx = Math.abs(s.x1 - s.x0);
                        const ry = Math.abs(s.y1 - s.y0);
                        ctx.ellipse(x0, y0, rx, ry, 0, 0, 2 * Math.PI);
                    }
                    ctx.fill();
                    ctx.stroke();
                    break;

                case "triangle":
                    ctx.moveTo(x0, y0);
                    ctx.lineTo(x1, y0);
                    ctx.lineTo((x0 + x1) / 2, y1);
                    ctx.closePath();
                    ctx.fill();
                    ctx.stroke();
                    break;

                case "polygon":
                    const sides = 5;
                    const radius = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                    const step = (2 * Math.PI) / sides;
                    for (let i = 0; i < sides; i++) {
                        const ang = i * step - Math.PI / 2;
                        const px = s.x0 + radius * Math.cos(ang) + offsetX;
                        const py = s.y0 + radius * Math.sin(ang) + offsetY;
                        i === 0 ? ctx.moveTo(px, py) : ctx.lineTo(px, py);
                    }
                    ctx.closePath();
                    ctx.fill();
                    ctx.stroke();
                    break;
                case "poly-line":
                    ctx.beginPath();
                    const pts = s.points;
                    ctx.moveTo(pts[0].x + offsetX, pts[0].y + offsetY);
                    for (let i = 1; i < pts.length; i++) {
                        ctx.lineTo(pts[i].x + offsetX, pts[i].y + offsetY);
                    }
                    ctx.stroke();
                    break;
                case "curve":
                    ctx.beginPath();

                    const pts2 = s.points;
                    ctx.moveTo(pts2[0].x + offsetX, pts2[0].y + offsetY);

                    for (let i = 1; i < pts2.length; i++) {
                        const prev = pts2[i - 1], curr = pts2[i];
                        const mx = (prev.x + curr.x) / 2 + offsetX,
                            my = (prev.y + curr.y) / 2 + offsetY;
                        ctx.quadraticCurveTo(
                            prev.x + offsetX, prev.y + offsetY,
                            mx, my
                        );
                    }

                    // finish at last point
                    const last = pts2[pts2.length - 1];
                    ctx.lineTo(last.x + offsetX, last.y + offsetY);

                    ctx.stroke();
                    ctx.restore();
                    break;
            }

            // 3) if shape is selected, draw red bounding box
            if (s.selected) {
                ctx.save();
                ctx.strokeStyle = "red";
                ctx.lineWidth = 1;

                // 1) Compute the overall bounding box for this shape
                let xMin = Infinity, yMin = Infinity, xMax = -Infinity, yMax = -Infinity;

                if (s.points) {
                    // poly-line or curve: box around all control points
                    s.points.forEach(pt => {
                        xMin = Math.min(xMin, pt.x);
                        yMin = Math.min(yMin, pt.y);
                        xMax = Math.max(xMax, pt.x);
                        yMax = Math.max(yMax, pt.y);
                    });
                } else if (s.tool === "circle") {
                    // circle/ellipse: box around full radii
                    const cx = s.x0, cy = s.y0;
                    if (s.isCircle) {
                        const r = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                        xMin = cx - r; yMin = cy - r;
                        xMax = cx + r; yMax = cy + r;
                    } else {
                        const rx = Math.abs(s.x1 - s.x0), ry = Math.abs(s.y1 - s.y0);
                        xMin = cx - rx; yMin = cy - ry;
                        xMax = cx + rx; yMax = cy + ry;
                    }
                } else  if (s.tool === "line"){
                    // bounding box exactly fits the line
                    xMin = p0.x; yMin= p0.y; xMax= p1.x; yMax= p1.y;
                } else {
                    // line or rectangle‐based shape: box from (x0,y0) to (x1,y1)
                    xMin = Math.min(s.x0, s.x1);
                    yMin = Math.min(s.y0, s.y1);
                    xMax = Math.max(s.x0, s.x1);
                    yMax = Math.max(s.y0, s.y1);
                }

                // 2) Draw the bounding rectangle
                ctx.strokeRect(
                    xMin, yMin,
                    xMax - xMin,
                    yMax - yMin
                );

                // 3) Draw 4 corner handles
                const R = 5;
                const corners = [
                    { x: xMin, y: yMin },
                    { x: xMax, y: yMin },
                    { x: xMax, y: yMax },
                    { x: xMin, y: yMax },
                ];

                corners.forEach(({ x, y }) => {
                    ctx.beginPath();
                    ctx.arc(x, y, R, 0, 2 * Math.PI);
                    ctx.fillStyle = "white";
                    ctx.fill();
                    ctx.stroke();
                });

                ctx.restore();
            }





            ctx.restore();
        });
    }

    // 4) Drawing & selection logic
    useEffect(() => {
        const c2 = c2Ref.current;
        const ctx2 = c2.getContext("2d");

        const getPos = e => {
            const r = c2.getBoundingClientRect();
            return { x: e.clientX - r.left, y: e.clientY - r.top };
        };

        const down = e => {
            if (activeTool === "cursor") {
                const pos = getPos(e);
                const ctx1 = c1Ref.current.getContext("2d");

                // clear prior selection
                shapesRef.current.forEach(s => s.selected = false);

                // iterate top-down
                for (let i = shapesRef.current.length - 1; i >= 0; i--) {
                    const s = shapesRef.current[i];
                    const path = new Path2D();

                    // bump the stroke width for hit detection
                    ctx1.lineWidth = (s.strokeWidth || strokeWidth) + 8;

                    switch (s.tool) {
                        case "line":
                            path.moveTo(s.x0, s.y0);
                            path.lineTo(s.x1, s.y1);
                            if (ctx1.isPointInStroke(path, pos.x, pos.y)) {
                                s.selected = true;
                            }
                            break;

                        case "poly-line":
                        case "curve":
                            const pts = s.points || [];
                            if (pts.length > 1) {
                                path.moveTo(pts[0].x, pts[0].y);
                                for (let j = 1; j < pts.length; j++) {
                                    path.lineTo(pts[j].x, pts[j].y);
                                }
                                if (ctx1.isPointInStroke(path, pos.x, pos.y)) {
                                    s.selected = true;
                                }
                            }
                            break;

                        case "triangle":
                            // same vertices you draw with
                            path.moveTo(s.x0, s.y0);
                            path.lineTo(s.x1, s.y0);
                            path.lineTo((s.x0 + s.x1) / 2, s.y1);
                            path.closePath();
                            if (ctx1.isPointInPath(path, pos.x, pos.y)) {
                                s.selected = true;
                            }
                            break;

                        case "square":
                        case "rectangle":
                            const rx = Math.min(s.x0, s.x1),
                                ry = Math.min(s.y0, s.y1),
                                rw = Math.abs(s.x1 - s.x0),
                                rh = Math.abs(s.y1 - s.y0);
                            path.rect(rx, ry, rw, rh);
                            if (ctx1.isPointInPath(path, pos.x, pos.y)) {
                                s.selected = true;
                            }
                            break;

                        case "circle":
                            const cx = s.x0, cy = s.y0;
                            if (s.isCircle) {
                                const r = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                                path.arc(cx, cy, r, 0, 2 * Math.PI);
                            } else {
                                const rx2 = Math.abs(s.x1 - s.x0),
                                    ry2 = Math.abs(s.y1 - s.y0);
                                path.ellipse(cx, cy, rx2, ry2, 0, 0, 2 * Math.PI);
                            }
                            if (ctx1.isPointInPath(path, pos.x, pos.y)) {
                                s.selected = true;
                            }
                            break;

                        case "polygon":
                            const sides = 5,
                                pcx = s.x0, pcy = s.y0,
                                rad = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                            for (let k = 0; k < sides; k++) {
                                const ang = k * (2 * Math.PI / sides) - Math.PI / 2,
                                    px = pcx + rad * Math.cos(ang),
                                    py = pcy + rad * Math.sin(ang);
                                k === 0 ? path.moveTo(px, py) : path.lineTo(px, py);
                            }
                            path.closePath();
                            if (ctx1.isPointInPath(path, pos.x, pos.y)) {
                                s.selected = true;
                            }
                            break;
                    }

                    if (s.selected) {
                        redrawAll();
                        return;  // stop at the topmost hit
                    }
                }

                // nothing hit
                redrawAll();
                return;
            }

            if (activeTool === "poly-line") {
                polyRef.current.push(getPos(e));
                setIsDrawing(true);
                return;
            }
            if (activeTool === "curve") {
                // add a new control-point
                curveRef.current.push(getPos(e));
                setIsDrawing(true);
                return;
            }
            if (!["square", "line", "circle", "triangle", "polygon"].includes(activeTool)) return;
            setStartPos(getPos(e));
            setIsDrawing(true);
        };

        const move = e => {
            const pos = getPos(e);
            // — Poly-line preview (draw all saved verts + the rubber-band) —
            if (activeTool === "poly-line" && polyRef.current.length) {
                ctx2.clearRect(0, 0, c2.width, c2.height);
                ctx2.strokeStyle = lineColor;
                ctx2.lineWidth = strokeWidth;
                ctx2.beginPath();

                // 1) move to first vertex
                ctx2.moveTo(polyRef.current[0].x, polyRef.current[0].y);

                // 2) draw each existing segment
                for (let i = 1; i < polyRef.current.length; i++) {
                    const pt = polyRef.current[i];
                    ctx2.lineTo(pt.x, pt.y);
                }

                // 3) rubber-band from last vertex to cursor
                ctx2.lineTo(pos.x, pos.y);

                ctx2.stroke();
                return;
            }

            if (activeTool === "curve" && curveRef.current.length) {
                ctx2.clearRect(0, 0, c2.width, c2.height);
                ctx2.strokeStyle = lineColor;
                ctx2.lineWidth = strokeWidth;
                ctx2.beginPath();

                // start at first point
                const pts = curveRef.current;
                ctx2.moveTo(pts[0].x, pts[0].y);

                // for each segment, do quadratic through midpoint
                for (let i = 1; i < pts.length; i++) {
                    const prev = pts[i - 1], curr = pts[i];
                    const mx = (prev.x + curr.x) / 2,
                        my = (prev.y + curr.y) / 2;
                    ctx2.quadraticCurveTo(prev.x, prev.y, mx, my);
                }

                // final “rubber band” from last control-point to cursor
                const last = pts[pts.length - 1];
                const mx2 = (last.x + pos.x) / 2,
                    my2 = (last.y + pos.y) / 2;
                ctx2.quadraticCurveTo(last.x, last.y, mx2, my2);
                ctx2.lineTo(pos.x, pos.y);

                ctx2.stroke();
                return;
            }


            if (!isDrawing) return;

            const { x, y } = getPos(e);
            const dx = x - startPos.x, dy = y - startPos.y;
            ctx2.clearRect(0, 0, c2.width, c2.height);
            ctx2.strokeStyle = lineColor;
            ctx2.lineWidth = strokeWidth;
            ctx2.beginPath();
            switch (activeTool) {
                case "square":
                    const isSquare = e.shiftKey;
                    if (isSquare) {
                        const side = Math.min(Math.abs(dx), Math.abs(dy));
                        const w = dx < 0 ? -side : side;
                        const h = dy < 0 ? -side : side;
                        ctx2.strokeRect(startPos.x, startPos.y, w, h);
                    } else { ctx2.strokeRect(startPos.x, startPos.y, dx, dy); }
                    break;
                case "line":
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(x, y);
                    ctx2.stroke();
                    break;
                case "circle":
                    const isCircle = e.shiftKey;
                    if (isCircle) {
                        // perfect circle
                        const r = Math.min(Math.abs(dx), Math.abs(dy));
                        ctx2.ellipse(startPos.x, startPos.y, r, r, 0, 0, 2 * Math.PI);
                    } else {
                        // free‐form ellipse
                        ctx2.ellipse(startPos.x, startPos.y, Math.abs(dx), Math.abs(dy), 0, 0, 2 * Math.PI);
                    }
                    ctx2.stroke()
                    break;
                case "triangle":
                    ctx2.moveTo(startPos.x, startPos.y);
                    ctx2.lineTo(startPos.x + dx, startPos.y);
                    ctx2.lineTo(startPos.x + dx / 2, startPos.y + dy);
                    ctx2.closePath();
                    ctx2.stroke();
                    break;
                case "polygon":
                    const sides2 = 5, rad2 = Math.hypot(dx, dy), step2 = 2 * Math.PI / sides2;
                    for (let i = 0; i < sides2; i++) {
                        const ang = i * step2 - Math.PI / 2,
                            px = startPos.x + rad2 * Math.cos(ang),
                            py = startPos.y + rad2 * Math.sin(ang);
                        i === 0 ? ctx2.moveTo(px, py) : ctx2.lineTo(px, py);
                    }
                    ctx2.closePath();
                    ctx2.stroke();
                    break;
            }

        };

        const up = e => {
            if (activeTool === "poly-line" || activeTool === "curve") return;
            if (!isDrawing) return;
            const { x, y } = getPos(e);
            const isCircle = activeTool === "circle" && e.shiftKey;
            const isSquare = activeTool === "square" && e.shiftKey;
            shapesRef.current.push({
                tool: activeTool,
                x0: startPos.x, y0: startPos.y,
                x1: x, y1: y,
                lineColor, fillColor, strokeWidth,
                isCircle, isSquare,
                selected: false
            });
            redrawAll();
            ctx2.clearRect(0, 0, c2.width, c2.height);
            setIsDrawing(false);
        };

        const finishPoly = e => {
            if (activeTool !== "poly-line" || polyRef.current.length < 2) return;
            shapesRef.current.push({
                tool: "poly-line",
                points: polyRef.current.slice(),
                lineColor, fillColor, strokeWidth,
                selected: false
            });
            redrawAll();
            ctx2.clearRect(0, 0, c2.width, c2.height);
            polyRef.current = [];
        };

        const finishCurve = e => {
            if (activeTool !== "curve" || curveRef.current.length < 2) return;
            shapesRef.current.push({
                tool: "curve",
                points: curveRef.current.slice(),
                lineColor, fillColor, strokeWidth,
                selected: false
            });
            redrawAll();
            ctx2.clearRect(0, 0, c2.width, c2.height);
            curveRef.current = [];
            setIsDrawing(false);
        };

        const onKeyDown = e => {
            if (e.ctrlKey && e.key === "z" && shapesRef.current.length) {
                redoRef.current.push(shapesRef.current.pop());
                redrawAll();
            }
            else if (e.ctrlKey && e.shiftKey && e.key.toLowerCase() === "z" && redoRef.current.length) {
                shapesRef.current.push(redoRef.current.pop());
                redrawAll();
            }
        };

        c2.addEventListener("mousedown", down);
        c2.addEventListener("mousemove", move);
        c2.addEventListener("mouseup", up);
        c2.addEventListener("dblclick", finishPoly);
        c2.addEventListener("dblclick", finishCurve);
        window.addEventListener("keydown", onKeyDown);
        return () => {
            c2.removeEventListener("mousedown", down);
            c2.removeEventListener("mousemove", move);
            c2.removeEventListener("mouseup", up);
            c2.removeEventListener("dblclick", finishPoly);
            c2.removeEventListener("dblclick", finishCurve);
            window.removeEventListener("keydown", onKeyDown);
        };
    }, [activeTool, isDrawing, startPos, lineColor, fillColor, strokeWidth]);

    return (
        <div ref={containerRef} className="canvas-container">
            <canvas ref={c1Ref} />
            <canvas ref={c2Ref} style={{ backgroundColor: "transparent" }} />
        </div>
    );
});

export default Canvas;
