import React, { forwardRef, useImperativeHandle, useRef, useEffect, useState } from "react";
import { jsPDF } from "jspdf";
import "../css/canvas.css";

// --- Helper Functions ---
// Draw a uniform grid of given cell size
function drawGrid(ctx, width, height, cell) {
    ctx.save();
    ctx.beginPath();
    ctx.strokeStyle = "#ffffff3a";    // light grey grid
    ctx.lineWidth = 1;
    // vertical
    for (let x = 0; x <= width; x += cell) {
        ctx.moveTo(x, 0);
        ctx.lineTo(x, height);
    }
    // horizontal
    for (let y = 0; y <= height; y += cell) {
        ctx.moveTo(0, y);
        ctx.lineTo(width, y);
    }
    ctx.stroke();
    ctx.restore();
}

function getBBox(shape) {
    // 1) Gather the raw, un-rotated points for every tool:
    let pts = [];

    if (shape.tool === "line") {
        // simple two-endpoint line
        pts = [
            { x: shape.x0, y: shape.y0 },
            { x: shape.x1, y: shape.y1 }
        ];
    }
    else if (shape.points) {
        // poly-line or curve
        pts = shape.points.map(p => ({ x: p.x, y: p.y }));
    }
    else if (shape.tool === "circle") {
        const cx = shape.x0, cy = shape.y0;
        if (shape.isCircle) {
            // perfect circle → use its bounding square
            const r = Math.hypot(shape.x1 - shape.x0, shape.y1 - shape.y0);
            pts = [
                { x: cx - r, y: cy - r },
                { x: cx + r, y: cy - r },
                { x: cx + r, y: cy + r },
                { x: cx - r, y: cy + r }
            ];
        } else {
            // ellipse → axis-aligned radii
            const rx = Math.abs(shape.x1 - shape.x0);
            const ry = Math.abs(shape.y1 - shape.y0);
            pts = [
                { x: cx - rx, y: cy - ry },
                { x: cx + rx, y: cy - ry },
                { x: cx + rx, y: cy + ry },
                { x: cx - rx, y: cy + ry }
            ];
        }
    }
    else if (shape.tool === "polygon") {
        // regular pentagon (5 sides) about center (x0,y0)
        const cx = shape.x0, cy = shape.y0;
        const radius = Math.hypot(shape.x1 - shape.x0, shape.y1 - shape.y0);
        const sides = 5;
        for (let i = 0; i < sides; i++) {
            const ang = i * (2 * Math.PI / sides) - Math.PI / 2;
            pts.push({
                x: cx + radius * Math.cos(ang),
                y: cy + radius * Math.sin(ang)
            });
        }
    }
    else {
        // square or rectangle
        let xA = shape.x0, yA = shape.y0;
        let xB = shape.x1, yB = shape.y1;

        if (shape.tool === "square" && shape.isSquare) {
            // lock to perfect square
            const dx = shape.x1 - shape.x0;
            const dy = shape.y1 - shape.y0;
            const side = Math.min(Math.abs(dx), Math.abs(dy));
            xB = shape.x0 + (dx < 0 ? -side : side);
            yB = shape.y0 + (dy < 0 ? -side : side);
        }

        // the four corners of the box
        pts = [
            { x: Math.min(xA, xB), y: Math.min(yA, yB) },
            { x: Math.max(xA, xB), y: Math.min(yA, yB) },
            { x: Math.max(xA, xB), y: Math.max(yA, yB) },
            { x: Math.min(xA, xB), y: Math.max(yA, yB) }
        ];
    }

    // 2) If rotated, spin each point around its center:
    const θ = shape.rotation || 0;
    if (θ !== 0) {
        // find the geometric center of pts
        const cx = pts.reduce((acc, p) => acc + p.x, 0) / pts.length;
        const cy = pts.reduce((acc, p) => acc + p.y, 0) / pts.length;
        const cos = Math.cos(θ), sin = Math.sin(θ);

        pts = pts.map(p => {
            const dx = p.x - cx, dy = p.y - cy;
            return {
                x: dx * cos - dy * sin + cx,
                y: dx * sin + dy * cos + cy
            };
        });
    }

    // 3) Finally, compute axis-aligned extents:
    const xs = pts.map(p => p.x);
    const ys = pts.map(p => p.y);
    return {
        xMin: Math.min(...xs),
        yMin: Math.min(...ys),
        xMax: Math.max(...xs),
        yMax: Math.max(...ys)
    };
}



// Hit-test a point against any shape (strokes for lines, box for others)
function hitTest(ctx, shape, x, y, defaultStroke) {
    const path = new Path2D();
    ctx.lineWidth = (shape.strokeWidth || defaultStroke) + 8;
    switch (shape.tool) {
        case "line":
            path.moveTo(shape.x0, shape.y0);
            path.lineTo(shape.x1, shape.y1);
            return ctx.isPointInStroke(path, x, y);
        case "poly-line":
        case "curve":
            if (shape.points && shape.points.length > 1) {
                path.moveTo(shape.points[0].x, shape.points[0].y);
                for (let p of shape.points.slice(1)) path.lineTo(p.x, p.y);
                return ctx.isPointInStroke(path, x, y);
            }
            return false;
        default:
            const { xMin, yMin, xMax, yMax } = getBBox(shape);
            return x >= xMin && x <= xMax && y >= yMin && y <= yMax;
    }
}

// Draw a shape onto the canvas context
function drawShape(ctx, shape, offsetX = 0, offsetY = 0) {
    // compute raw coords and bounding-box center
    const x0 = shape.x0 + offsetX;
    const y0 = shape.y0 + offsetY;
    const x1 = shape.x1 + offsetX;
    const y1 = shape.y1 + offsetY;
    const { xMin, yMin, xMax, yMax } = getBBox(shape);
    const cx = (xMin + xMax) / 2 + offsetX;
    const cy = (yMin + yMax) / 2 + offsetY;

    // 1) push state
    ctx.save();

    // 2) apply rotation around center
    ctx.translate(cx, cy);
    ctx.rotate(shape.rotation || 0);
    ctx.translate(-cx, -cy);

    // 3) draw each tool
    switch (shape.tool) {

        case "square": {
            const dx = shape.x1 - shape.x0;
            const dy = shape.y1 - shape.y0;
            let w, h;
            if (shape.isSquare) {
                const side = Math.min(Math.abs(dx), Math.abs(dy));
                w = dx < 0 ? -side : side;
                h = dy < 0 ? -side : side;
            } else {
                w = dx;
                h = dy;
            }
            ctx.fillRect(shape.x0 + offsetX, shape.y0 + offsetY, w, h);
            ctx.strokeRect(shape.x0 + offsetX, shape.y0 + offsetY, w, h);
            break;
        }

        case "line":
            ctx.beginPath();
            ctx.moveTo(x0, y0);
            ctx.lineTo(x1, y1);
            ctx.stroke();
            break;

        case "circle":
            ctx.beginPath();
            if (shape.isCircle) {
                const r = Math.hypot(shape.x1 - shape.x0, shape.y1 - shape.y0);
                ctx.arc(x0, y0, r, 0, 2 * Math.PI);
            } else {
                ctx.ellipse(x0, y0, Math.abs(shape.x1 - shape.x0), Math.abs(shape.y1 - shape.y0), 0, 0, 2 * Math.PI);
            }
            ctx.fill();
            ctx.stroke();
            break;

        case "triangle":
            ctx.beginPath();
            ctx.moveTo(x0, y0);
            ctx.lineTo(x1, y0);
            ctx.lineTo((x0 + x1) / 2, y1);
            ctx.closePath();
            ctx.fill();
            ctx.stroke();
            break;

        case "polygon": {
            ctx.beginPath();
            const sides = 5;
            const radius = Math.hypot(shape.x1 - shape.x0, shape.y1 - shape.y0);
            for (let i = 0; i < sides; i++) {
                const ang = i * (2 * Math.PI / sides) - Math.PI / 2;
                const px = shape.x0 + offsetX + radius * Math.cos(ang);
                const py = shape.y0 + offsetY + radius * Math.sin(ang);
                if (i === 0) ctx.moveTo(px, py);
                else ctx.lineTo(px, py);
            }
            ctx.closePath();
            ctx.fill();
            ctx.stroke();
            break;
        }

        case "poly-line":
            if (shape.points?.length > 1) {
                ctx.beginPath();
                ctx.moveTo(shape.points[0].x + offsetX, shape.points[0].y + offsetY);
                for (let p of shape.points.slice(1)) {
                    ctx.lineTo(p.x + offsetX, p.y + offsetY);
                }
                ctx.stroke();
            }
            break;

        case "curve":
            if (shape.points?.length > 1) {
                ctx.beginPath();
                const pts = shape.points;
                ctx.moveTo(pts[0].x + offsetX, pts[0].y + offsetY);
                for (let i = 1; i < pts.length; i++) {
                    const prev = pts[i - 1], curr = pts[i];
                    const mx = (prev.x + curr.x) / 2 + offsetX;
                    const my = (prev.y + curr.y) / 2 + offsetY;
                    ctx.quadraticCurveTo(
                        prev.x + offsetX, prev.y + offsetY,
                        mx, my
                    );
                }
                const last = pts[pts.length - 1];
                ctx.lineTo(last.x + offsetX, last.y + offsetY);
                ctx.stroke();
            }
            break;

        default:
            // if you have any other shape types, handle them here
            break;
    }

    // 4) restore state so next shape draws from identity
    ctx.restore();
}


// Draw selection handles
function drawSelection(ctx, shape, offsetX = 0, offsetY = 0) {
    const R = 5;
    if (shape.tool === "line") {
        const p0 = { x: shape.x0 + offsetX, y: shape.y0 + offsetY };
        const p1 = { x: shape.x1 + offsetX, y: shape.y1 + offsetY };
        ctx.fillStyle = "white";
        ctx.fill();
        ctx.beginPath(); ctx.moveTo(p0.x, p0.y); ctx.lineTo(p1.x, p1.y); ctx.stroke();
        [p0, p1].forEach(pt => {
            ctx.beginPath(); ctx.arc(pt.x, pt.y, R, 0, 2 * Math.PI); ctx.fill(); ctx.stroke();
        });
        return;
    }
    const { xMin, yMin, xMax, yMax } = getBBox(shape);
    const midX = (xMin + xMax) / 2;
    const rotY = yMin - 20;
    ctx.beginPath();
    ctx.moveTo(midX, yMin);
    ctx.lineTo(midX, rotY);
    ctx.stroke();

    // circle grip
    ctx.beginPath();
    ctx.arc(midX, rotY, R, 0, 2 * Math.PI);

    ctx.stroke();
    ctx.strokeRect(xMin, yMin, xMax - xMin, yMax - yMin);
    ctx.fillStyle = "white";
    ctx.fill();
    [
        { x: xMin, y: yMin },
        { x: xMax, y: yMin },
        { x: xMax, y: yMax },
        { x: xMin, y: yMax }
    ].forEach(pt => {
        ctx.beginPath(); ctx.arc(pt.x, pt.y, R, 0, 2 * Math.PI); ctx.fill(); ctx.stroke();
    });
}

const Canvas = forwardRef(({ activeTool, lineColor, fillColor, strokeWidth, showGrid, snapToGrid }, ref) => {
    const containerRef = useRef(null);
    const c1Ref = useRef(null);
    const c2Ref = useRef(null);
    const shapesRef = useRef([]);
    const undoStack = useRef([]);
    const redoStack = useRef([]);
    const polyRef = useRef([]);
    const curveRef = useRef([]);
    const [isDrawing, setIsDrawing] = useState(false);
    const [startPos, setStartPos] = useState({ x: 0, y: 0 });
    const dragRef = useRef({
        isDragging: false,
        isResizing: false,
        prevPos: { x: 0, y: 0 },
        shapeIndex: null,
        handleIndex: null,
        origShape: null,
        origRotation: 0,
        center: null,
        startAngle: 0
    });
    const gridSize = 100; // match the same cell size you draw in drawGrid
    const snap = ({ x, y }) => ({
        x: Math.round(x / gridSize) * gridSize,
        y: Math.round(y / gridSize) * gridSize
    });

    useImperativeHandle(ref, () => ({
        exportShapes: () => {
            shapesRef.current.forEach(s => (s.selected = false));
            const data = JSON.stringify(shapesRef.current, null, 2);
            const blob = new Blob([data], { type: "application/json" });
            const url = URL.createObjectURL(blob);
            const a = document.createElement("a");
            a.href = url; a.download = "shapes.json"; a.click();
            URL.revokeObjectURL(url);
        },
        downloadJPEG: () => {
            shapesRef.current.forEach(s => (s.selected = false));
            const shapes = shapesRef.current;
            const pad = 20;
            let minX = Infinity, minY = Infinity;
            let maxX = -Infinity, maxY = -Infinity;
            shapes.forEach(s => {
                if (s.tool === "circle" || s.tool === "polygon") {
                    const r = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                    minX = Math.min(minX, s.x0 - r);
                    maxX = Math.max(maxX, s.x0 + r);
                    minY = Math.min(minY, s.y0 - r);
                    maxY = Math.max(maxY, s.y0 + r);
                } else {
                    minX = Math.min(minX, s.x0, s.x1);
                    maxX = Math.max(maxX, s.x0, s.x1);
                    minY = Math.min(minY, s.y0, s.y1);
                    maxY = Math.max(maxY, s.y0, s.y1);
                }
            });
            if (minX === Infinity) {
                minX = 0; minY = 0;
                maxX = c1Ref.current.width;
                maxY = c1Ref.current.height;
            }
            minX -= pad; minY -= pad; maxX += pad; maxY += pad;
            const sw = maxX - minX;
            const sh = maxY - minY;
            const tmp = document.createElement("canvas");
            tmp.width = sw; tmp.height = sh;
            redrawAll({ current: tmp }, -minX, -minY, "#ffffff");
            const dataURL = tmp.toDataURL("image/jpeg", 1.0);
            const link = document.createElement("a");
            link.href = dataURL;
            link.download = "shapes.jpg";
            link.click();
        },
        downloadPDF: () => {
            shapesRef.current.forEach(s => (s.selected = false));
            const shapes = shapesRef.current;
            const pad = 20;
            let minX = Infinity, minY = Infinity;
            let maxX = -Infinity, maxY = -Infinity;
            shapes.forEach(s => {
                if (s.tool === "circle" || s.tool === "polygon") {
                    const r = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                    minX = Math.min(minX, s.x0 - r);
                    maxX = Math.max(maxX, s.x0 + r);
                    minY = Math.min(minY, s.y0 - r);
                    maxY = Math.max(maxY, s.y0 + r);
                } else {
                    minX = Math.min(minX, s.x0, s.x1);
                    maxX = Math.max(maxX, s.x0, s.x1);
                    minY = Math.min(minY, s.y0, s.y1);
                    maxY = Math.max(maxY, s.y0, s.y1);
                }
            });
            if (minX === Infinity) {
                minX = 0; minY = 0;
                maxX = c1Ref.current.width;
                maxY = c1Ref.current.height;
            }
            minX -= pad; minY -= pad; maxX += pad; maxY += pad;
            const sw = maxX - minX;
            const sh = maxY - minY;
            const tmp = document.createElement("canvas");
            tmp.width = sw; tmp.height = sh;
            redrawAll({ current: tmp }, -minX, -minY, "#ffffff");
            const orientation = sw > sh ? "landscape" : "portrait";
            const pdf = new jsPDF({ orientation, unit: "px", format: "a4" });
            const pageW = pdf.internal.pageSize.getWidth();
            const pageH = pdf.internal.pageSize.getHeight();
            const scale = Math.min(pageW / sw, pageH / sh);
            let yOffset = 0;
            while (yOffset < sh) {
                const sliceH = Math.min(sh - yOffset, pageH / scale);
                const slice = document.createElement("canvas");
                slice.width = sw; slice.height = sliceH;
                slice.getContext("2d").drawImage(
                    tmp,
                    0, yOffset, sw, sliceH,
                    0, 0, sw, sliceH
                );
                const imgData = slice.toDataURL("image/jpeg", 1.0);
                const imgW = sw * scale, imgH = sliceH * scale;
                const xPos = (pageW - imgW) / 2;
                pdf.addImage(imgData, "JPEG", xPos, 0, imgW, imgH);
                yOffset += sliceH;
                if (yOffset < sh) pdf.addPage();
            }
            pdf.save("shapes.pdf");
        },
        loadShapes: (arr) => { shapesRef.current = arr.map(s => ({ ...s, selected: false })); redrawAll(); },
        getShapeCount: () => shapesRef.current.length,
        clearShapes: () => { shapesRef.current = []; redrawAll(); }
    }));

    useEffect(() => {
        const resize = () => {
            const { width, height } = containerRef.current.getBoundingClientRect();
            [c1Ref, c2Ref].forEach(r => { r.current.width = width; r.current.height = height; });
            redrawAll();
        };
        resize(); window.addEventListener("resize", resize);
        return () => window.removeEventListener("resize", resize);
    }, []);

    useEffect(() => {
        let dirty = false;
        shapesRef.current.forEach(s => {
            if (s.selected && (s.strokeWidth !== strokeWidth || s.lineColor !== lineColor || s.fillColor !== fillColor)) {
                s.strokeWidth = strokeWidth;
                s.lineColor = lineColor;
                s.fillColor = fillColor;
                dirty = true;
            }
        });
        if (dirty) redrawAll();
    }, [strokeWidth, lineColor, fillColor]);

    function redrawAll(context = c1Ref, offsetX = 0, offsetY = 0, backgroundColor = "#979797") {
        const c = context.current;
        const ctx = c.getContext("2d");
        ctx.clearRect(0, 0, c.width, c.height);
        // 1) paint background
        ctx.fillStyle = backgroundColor;
        ctx.fillRect(0, 0, c.width, c.height);
        // 2) draw grid behind everything
        if (showGrid) drawGrid(ctx, c.width, c.height, 100);   // 50px cells, tweak as you like
        shapesRef.current.forEach(s => {
            ctx.save();
            ctx.strokeStyle = s.lineColor || lineColor;
            ctx.fillStyle = s.fillColor || fillColor;
            ctx.lineWidth = s.strokeWidth || strokeWidth;
            drawShape(ctx, s, offsetX, offsetY);
            if (s.selected) {
                ctx.save(); ctx.strokeStyle = "#ff7300"; ctx.lineWidth = 2;
                drawSelection(ctx, s, offsetX, offsetY);
                ctx.restore();
            }
            ctx.restore();
        });
    }
    useEffect(() => {
        redrawAll();
    }, [showGrid]);

    useEffect(() => {
        const c2 = c2Ref.current;
        const ctx2 = c2.getContext("2d");
        const getPos = e => {
            const r = c2.getBoundingClientRect();
            return { x: e.clientX - r.left, y: e.clientY - r.top };
        };

        const down = e => {
            let pos = getPos(e);
            if (activeTool === "cursor") {
                const ctx1 = c1Ref.current.getContext("2d");
                // clear previous selection
                shapesRef.current.forEach(sh => sh.selected = false);

                // walk shapes in reverse z-order
                for (let i = shapesRef.current.length - 1; i >= 0; i--) {
                    const s = shapesRef.current[i];

                    const { xMin, yMin, xMax, yMax } = getBBox(s);
                    const midX = (xMin + xMax) / 2;
                    const rotY = yMin - 20;

                    if (Math.hypot(pos.x - midX, pos.y - rotY) < 6) {
                        console.log("🔄 starting rotate for shape", i);
                        s.selected = true;

                        // remember rotation state
                        dragRef.current = {
                            isRotating: true,
                            shapeIndex: i,
                            origRotation: s.rotation || 0,
                            // use the bounding‐box center, not (x0+x1)/2
                            center: { x: midX, y: (yMin + yMax) / 2 },
                            startAngle: Math.atan2(
                                pos.y - ((yMin + yMax) / 2),
                                pos.x - midX
                            )
                        };

                        redrawAll();
                        return;    // bail out so you don’t also start a move or resize
                    }

                    if (!hitTest(ctx1, s, pos.x, pos.y, strokeWidth)) continue;

                    // 1) check for a corner-drag first
                    const corners = [
                        { x: xMin, y: yMin },
                        { x: xMax, y: yMin },
                        { x: xMax, y: yMax },
                        { x: xMin, y: yMax }
                    ];
                    for (let h = 0; h < corners.length; h++) {
                        const dx = pos.x - corners[h].x;
                        const dy = pos.y - corners[h].y;
                        if (Math.hypot(dx, dy) < 6) {        // inside handle radius
                            s.selected = true;
                            dragRef.current = {
                                isResizing: true,
                                shapeIndex: i,
                                handleIndex: h,
                                origShape: JSON.parse(JSON.stringify(s))
                            };
                            redrawAll();
                            return;  // don’t start a move-drag
                        }
                    }

                    // 2) if no corner, it’s a normal drag
                    s.selected = true;
                    dragRef.current = {
                        isDragging: true,
                        startPos: pos,                                // where drag began
                        origShape: JSON.parse(JSON.stringify(s)),     // deep copy of original coords
                        shapeIndex: i
                    };
                    redrawAll();
                    return;
                }

                // clicked on empty space
                redrawAll();
                return;
            }
            if (activeTool === "poly-line") { polyRef.current.push(pos); setIsDrawing(true); return; }
            if (activeTool === "curve") { curveRef.current.push(pos); setIsDrawing(true); return; }
            if (!["square", "line", "circle", "triangle", "polygon"].includes(activeTool)) return;
            setStartPos(pos);
            setIsDrawing(true);
        };

        const move = e => {
            let pos = getPos(e);
            if (dragRef.current.isRotating) {
                console.log("🖱️ rotating… angle delta =", (Math.atan2(pos.y - dragRef.current.center.y,
                    pos.x - dragRef.current.center.x)
                    - dragRef.current.startAngle));
                const { shapeIndex, origRotation, center, startAngle } = dragRef.current;
                const target = shapesRef.current[shapeIndex];
                const currPos = getPos(e);

                // how far we’ve turned
                const currentAngle = Math.atan2(currPos.y - center.y, currPos.x - center.x);
                target.rotation = origRotation + (currentAngle - startAngle);

                redrawAll();
                return;
            }
            if (dragRef.current.isResizing) {
                const { shapeIndex, handleIndex, origShape } = dragRef.current;
                const target = shapesRef.current[shapeIndex];
                const pos = getPos(e);             // current mouse
                const { xMin, yMin, xMax, yMax } = getBBox(origShape);
                const corners = [
                    { x: xMin, y: yMin },
                    { x: xMax, y: yMin },
                    { x: xMax, y: yMax },
                    { x: xMin, y: yMax }
                ];
                const origHandle = corners[handleIndex];
                const opposite = corners[(handleIndex + 2) % 4];

                if (origShape.tool === "line") {
                    // for lines, just drag one endpoint
                    if (handleIndex === 0) {
                        target.x0 = pos.x; target.y0 = pos.y;
                    } else {
                        target.x1 = pos.x; target.y1 = pos.y;
                    }
                } else if (origShape.tool === "circle" || origShape.tool === "polygon") {
                    // if you want perfect circle lock, compute r = hypot(...)
                    // but even for ellipses, you can just set the handle
                    target.x1 = pos.x;
                    target.y1 = pos.y;
                    redrawAll();
                    return;
                } else {
                    // for everything else, scale about the opposite corner
                    const sx = (pos.x - opposite.x) / (origHandle.x - opposite.x);
                    const sy = (pos.y - opposite.y) / (origHandle.y - opposite.y);

                    if (origShape.points) {
                        // poly‐line & curve: scale each point
                        target.points = origShape.points.map(p => ({
                            x: opposite.x + (p.x - opposite.x) * sx,
                            y: opposite.y + (p.y - opposite.y) * sy
                        }));
                    } else {
                        // x0/y0 and x1/y1 shapes (rect, circle, square, triangle, polygon)
                        ["x0", "y0", "x1", "y1"].forEach(k => {
                            const coord = k.startsWith("x") ? "x" : "y";
                            const o = origShape[k];
                            const off = opposite[coord];
                            target[k] = off + (o - off) * (coord === "x" ? sx : sy);
                        });
                    }
                }

                redrawAll();
                return;
            }
            if (activeTool === "cursor" && dragRef.current.isDragging) {
                const { startPos, origShape, shapeIndex } = dragRef.current;
                const target = shapesRef.current[shapeIndex];

                // 1) how far the mouse has moved since drag start
                const dx = pos.x - startPos.x;
                const dy = pos.y - startPos.y;

                // 2) move the shape by that delta
                if (origShape.points) {
                    // poly-line & curve
                    target.points = origShape.points.map(p => ({
                        x: p.x + dx,
                        y: p.y + dy
                    }));
                } else {
                    // x0/y0 & x1/y1 shapes
                    target.x0 = origShape.x0 + dx;
                    target.y0 = origShape.y0 + dy;
                    target.x1 = origShape.x1 + dx;
                    target.y1 = origShape.y1 + dy;
                }

                // 3) now snap the bounding‐box top-left to grid
                if (showGrid && e.shiftKey) {
                    const { xMin, yMin } = getBBox(target);
                    const { x: snapX, y: snapY } = snap({ x: xMin, y: yMin });
                    const offX = snapX - xMin;
                    const offY = snapY - yMin;

                    if (target.points) {
                        target.points.forEach(p => {
                            p.x += offX;
                            p.y += offY;
                        });
                    } else {
                        target.x0 += offX; target.y0 += offY;
                        target.x1 += offX; target.y1 += offY;
                    }
                }

                redrawAll();
                return;
            }

            if (activeTool === "poly-line" && polyRef.current.length) {
                ctx2.clearRect(0, 0, c2.width, c2.height);
                ctx2.strokeStyle = lineColor; ctx2.lineWidth = strokeWidth;
                ctx2.beginPath(); ctx2.moveTo(polyRef.current[0].x, polyRef.current[0].y);
                for (let p of polyRef.current.slice(1)) ctx2.lineTo(p.x, p.y);
                ctx2.lineTo(pos.x, pos.y); ctx2.stroke(); return;
            }
            if (activeTool === "curve" && curveRef.current.length) {
                ctx2.clearRect(0, 0, c2.width, c2.height);
                const pts = [...curveRef.current, pos];
                const shape = {
                    tool: "curve",
                    points: pts,
                    strokeWidth,
                    lineColor,
                };
                ctx2.lineWidth = shape.strokeWidth;
                ctx2.strokeStyle = shape.lineColor;
                drawShape(ctx2, shape, 0, 0);
                return;
            }
            if (!isDrawing) return;
            ctx2.clearRect(0, 0, c2.width, c2.height);
            const { x, y } = pos;

            const shape = {
                tool: activeTool,
                x0: startPos.x,
                y0: startPos.y,
                x1: x,
                y1: y,
                isSquare: e.shiftKey && activeTool === "square",
                isCircle: e.shiftKey && activeTool === "circle",
                strokeWidth: 1,
                lineColor: "black",
                fillColor: "transparent"
            }
            ctx2.fillStyle = shape.fillColor;
            ctx2.strokeStyle = shape.lineColor;
            ctx2.lineWidth = shape.strokeWidth;

            drawShape(ctx2, shape, 0, 0);
        };

        const up = e => {
            if (dragRef.current.isRotating) {
                // 1) grab rotation info
                const { shapeIndex, center } = dragRef.current;
                const shape = shapesRef.current[shapeIndex];

                dragRef.current.isRotating = false;

                redrawAll();
                return;
            }
            if (dragRef.current.isResizing) {
                dragRef.current.isResizing = false;
                dragRef.current.handleIndex = null;
                dragRef.current.origShape = null;
                return;
            }
            if (activeTool === "cursor" && dragRef.current.isDragging) {
                dragRef.current.isDragging = false;
                dragRef.current.shapeIndex = null;
                return;
            }
            if (["poly-line", "curve"].includes(activeTool)) return;
            if (!isDrawing) return;
            const pos = getPos(e);
            if (pos.x === startPos.x && pos.y === startPos.y) {
                ctx2.clearRect(0, 0, c2.width, c2.height);
                setIsDrawing(false);
                return;
            }
            const isCircle = activeTool === "circle" && e.shiftKey;
            const isSquare = activeTool === "square" && e.shiftKey;

            const newShape = {
                tool: activeTool,
                x0: startPos.x, y0: startPos.y,
                x1: pos.x, y1: pos.y,
                lineColor, fillColor, strokeWidth,
                isCircle, isSquare,
                rotation: 0,
                selected: false
            };

            shapesRef.current.push(newShape);

            undoStack.current.push({ type: 'add', shapes: [newShape] });
            // clear redoStack because it's a brand-new action
            redoStack.current = [];

            shapesRef.current.forEach(s => (s.selected = false));
            shapesRef.current[shapesRef.current.length - 1].selected = true;
            redrawAll();
            ctx2.clearRect(0, 0, c2.width, c2.height);
            setIsDrawing(false);
        };

        const finishPoly = e => {
            if (activeTool !== "poly-line" || polyRef.current.length < 2) return;
            const newShape = {
                tool: "poly-line",
                points: polyRef.current.slice(),
                lineColor, fillColor, strokeWidth,
                selected: false
            };
            shapesRef.current.push(newShape);

            undoStack.current.push({ type: 'add', shapes: [newShape] });
            // clear redoStack because it's a brand-new action
            redoStack.current = [];

            shapesRef.current.forEach(s => (s.selected = false));
            shapesRef.current[shapesRef.current.length - 1].selected = true;
            redrawAll();
            ctx2.clearRect(0, 0, c2.width, c2.height);
            polyRef.current = [];
        };

        const finishCurve = e => {
            if (activeTool !== "curve" || curveRef.current.length < 2) return;

            // 1. capture the final click
            const pos = getPos(e);
            curveRef.current.push(pos);

            // 2. push the full point list
            const newShape = {
                tool: "curve",
                points: curveRef.current.slice(),
                lineColor, fillColor, strokeWidth,
                selected: false
            };
            shapesRef.current.push(newShape);

            undoStack.current.push({ type: 'add', shapes: [newShape] });
            // clear redoStack because it's a brand-new action
            redoStack.current = [];

            // 3. rest stays the same
            shapesRef.current.forEach(s => (s.selected = false));
            shapesRef.current[shapesRef.current.length - 1].selected = true;
            redrawAll();
            ctx2.clearRect(0, 0, c2.width, c2.height);

            // 4. reset
            curveRef.current = [];
            setIsDrawing(false);
        };

        const onKeyDown = e => {
            if (e.ctrlKey && !e.shiftKey && e.key.toLowerCase() === 'z') {
                const action = undoStack.current.pop();
                if (!action) return;

                if (action.type === 'add') {
                    // remove the shape(s) that were added
                    shapesRef.current = shapesRef.current.filter(
                        s => !action.shapes.includes(s)
                    );
                } else if (action.type === 'delete') {
                    // re‐insert deleted shapes at their original positions
                    action.shapes.forEach((shape, i) => {
                        const idx = action.indices[i];
                        shapesRef.current.splice(idx, 0, shape);
                    });
                }

                redoStack.current.push(action);
                redrawAll();
            }

            // REDO
            else if (e.ctrlKey && e.shiftKey && e.key.toLowerCase() === 'z') {
                const action = redoStack.current.pop();
                if (!action) return;

                if (action.type === 'add') {
                    // re‐add those shapes
                    shapesRef.current.push(...action.shapes);
                } else if (action.type === 'delete') {
                    // re‐delete them
                    shapesRef.current = shapesRef.current.filter(
                        s => !action.shapes.includes(s)
                    );
                }

                undoStack.current.push(action);
                redrawAll();
            }
            else if (e.key === "Delete") {
                const deleted = shapesRef.current.filter(s => s.selected);
                if (deleted.length) {
                    // remember their original indices if you care about insert order:
                    const indices = deleted.map(s => shapesRef.current.indexOf(s));
                    shapesRef.current = shapesRef.current.filter(s => !s.selected);
                    undoStack.current.push({ type: 'delete', shapes: deleted, indices });
                    redoStack.current = [];
                    redrawAll();
                }
            }
        };

        c2.addEventListener("mousedown", down);
        c2.addEventListener("mousemove", move);
        c2.addEventListener("mouseup", up);
        c2.addEventListener("dblclick", finishPoly);
        c2.addEventListener("dblclick", finishCurve);
        window.addEventListener("keydown", onKeyDown);
        return () => {
            c2.removeEventListener("mousedown", down);
            c2.removeEventListener("mousemove", move);
            c2.removeEventListener("mouseup", up);
            c2.removeEventListener("dblclick", finishPoly);
            c2.removeEventListener("dblclick", finishCurve);
            window.removeEventListener("keydown", onKeyDown);
        };
    }, [activeTool, isDrawing, startPos, lineColor, fillColor, strokeWidth, showGrid, snapToGrid]);

    return (
        <div ref={containerRef} className="canvas-container">
            <canvas ref={c1Ref} />
            <canvas ref={c2Ref} style={{ backgroundColor: "transparent" }} />
        </div>
    );
});

export default Canvas;
