// src/components/Canvas.js
import React, {
    forwardRef,
    useImperativeHandle,
    useRef,
    useEffect,
    useState
} from "react";
import { jsPDF } from "jspdf";
import "../css/canvas.css";

const Canvas = forwardRef(
    ({ activeTool, lineColor, fillColor, strokeWidth }, ref) => {
        const containerRef = useRef(null);
        const c1Ref = useRef(null);
        const c2Ref = useRef(null);

        const shapesRef = useRef([]);
        const redoRef = useRef([]);

        const [isDrawing, setIsDrawing] = useState(false);
        const [startPos, setStartPos] = useState({ x: 0, y: 0 });
        const [enterDown, setEnterDown] = useState(false);

        useImperativeHandle(ref, () => ({
            exportShapes: () => {
                const data = JSON.stringify(shapesRef.current, null, 2);
                const blob = new Blob([data], { type: "application/json" });
                const url = URL.createObjectURL(blob);
                const a = document.createElement("a");
                a.href = url; a.download = "shapes.json"; a.click();
                URL.revokeObjectURL(url);
            },
            downloadJPEG: () => {
                // … your existing downloadJPEG implementation …
            },
            downloadPDF: () => {
                // … your existing downloadPDF implementation …
            },
            loadShapes: (arr) => {
                shapesRef.current = arr.map(s => ({ ...s, selected: false }));
                redrawAll();
            },
            getShapeCount: () => shapesRef.current.length,
            clearShapes: () => {
                shapesRef.current = [];
                redrawAll();
            }
        }));

        // Keep canvases sized to container
        useEffect(() => {
            const resize = () => {
                const { width, height } = containerRef.current.getBoundingClientRect();
                c1Ref.current.width = width;
                c1Ref.current.height = height;
                c2Ref.current.width = width;
                c2Ref.current.height = height;
                redrawAll();
            };
            resize();
            window.addEventListener("resize", resize);
            return () => window.removeEventListener("resize", resize);
        }, []);

        // Redraw all shapes, using ellipse for 'circle' tool
        function redrawAll(context = c1Ref, offsetX = 0, offsetY = 0, backgroundColor = "#979797") {
            const c = context.current;
            const ctx = c.getContext("2d");
            const w = c.width, h = c.height;

            // background
            ctx.clearRect(0, 0, w, h);
            ctx.fillStyle = backgroundColor;
            ctx.fillRect(0, 0, w, h);

            shapesRef.current.forEach(s => {
                const lc = s.lineColor || lineColor;
                const fc = s.fillColor || fillColor;
                const sw = s.strokeWidth ?? strokeWidth;
                ctx.save();
                ctx.strokeStyle = lc;
                ctx.fillStyle = fc;
                ctx.lineWidth = sw;
                ctx.beginPath();

                const x0 = s.x0 + offsetX;
                const y0 = s.y0 + offsetY;
                const x1 = s.x1 + offsetX;
                const y1 = s.y1 + offsetY;

                switch (s.tool) {
                    case "square":
                        ctx.fillRect(x0, y0, x1 - x0, y1 - y0);
                        ctx.strokeRect(x0, y0, x1 - x0, y1 - y0);
                        break;

                    case "line":
                        ctx.moveTo(x0, y0);
                        ctx.lineTo(x1, y1);
                        ctx.stroke();
                        break;

                    case "circle":
                        if (s.isCircle) {
                            // perfect circle
                            const r = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                            ctx.arc(x0, y0, r, 0, 2 * Math.PI);
                        } else {
                            // free-form ellipse
                            const rx = Math.abs(s.x1 - s.x0);
                            const ry = Math.abs(s.y1 - s.y0);
                            ctx.ellipse(x0, y0, rx, ry, 0, 0, 2 * Math.PI);
                        }
                        ctx.fill();
                        ctx.stroke();
                        break;

                    case "triangle":
                        ctx.moveTo(x0, y0);
                        ctx.lineTo(x1, y0);
                        ctx.lineTo((x0 + x1) / 2, y1);
                        ctx.closePath();
                        ctx.fill();
                        ctx.stroke();
                        break;

                    case "polygon":
                        const sides = 5;
                        const rad = Math.hypot(s.x1 - s.x0, s.y1 - s.y0);
                        const step = (2 * Math.PI) / sides;
                        for (let i = 0; i < sides; i++) {
                            const ang = i * step - Math.PI / 2;
                            const px = s.x0 + rad * Math.cos(ang) + offsetX;
                            const py = s.y0 + rad * Math.sin(ang) + offsetY;
                            i === 0 ? ctx.moveTo(px, py) : ctx.lineTo(px, py);
                        }
                        ctx.closePath();
                        ctx.fill();
                        ctx.stroke();
                        break;
                }

                if (s.selected) {
                    ctx.strokeStyle = "red";
                    ctx.strokeRect(
                        Math.min(s.x0, s.x1) + offsetX,
                        Math.min(s.y0, s.y1) + offsetY,
                        Math.abs(s.x1 - s.x0),
                        Math.abs(s.y1 - s.y0)
                    );
                }
                ctx.restore();
            });
        }

        // Drawing & preview logic
        useEffect(() => {
            const c2 = c2Ref.current;
            const ctx2 = c2.getContext("2d");

            const getPos = e => {
                const r = c2.getBoundingClientRect();
                return { x: e.clientX - r.left, y: e.clientY - r.top };
            };

            const down = e => {
                if (activeTool === "cursor") {
                    // selection logic…
                    redrawAll();
                    return;
                }
                if (!["square", "line", "circle", "triangle", "polygon"].includes(activeTool))
                    return;
                setStartPos(getPos(e));
                setIsDrawing(true);
            };

            const move = e => {
                if (!isDrawing) return;
                const { x, y } = getPos(e);
                const dx = x - startPos.x, dy = y - startPos.y;
                ctx2.clearRect(0, 0, c2.width, c2.height);
                ctx2.strokeStyle = lineColor;
                ctx2.lineWidth = strokeWidth;
                ctx2.beginPath();

                switch (activeTool) {
                    case "square":
                        ctx2.strokeRect(startPos.x, startPos.y, dx, dy);
                        break;
                    case "line":
                        ctx2.moveTo(startPos.x, startPos.y);
                        ctx2.lineTo(x, y);
                        ctx2.stroke();
                        break;
                    case "circle":
                        if (enterDown) {
                            // force circle
                            const r = Math.min(Math.abs(dx), Math.abs(dy));
                            ctx2.ellipse(startPos.x, startPos.y, r, r, 0, 0, 2 * Math.PI);
                            console.log("in circle");
                        } else {
                            // free ellipse
                            ctx2.ellipse(
                                startPos.x,
                                startPos.y,
                                Math.abs(dx),
                                Math.abs(dy),
                                0,
                                0,
                                2 * Math.PI
                            );
                        }
                        ctx2.stroke();
                        break;
                    case "triangle":
                        ctx2.moveTo(startPos.x, startPos.y);
                        ctx2.lineTo(startPos.x + dx, startPos.y);
                        ctx2.lineTo(startPos.x + dx / 2, startPos.y + dy);
                        ctx2.closePath();
                        ctx2.stroke();
                        break;
                    case "polygon":
                        const sides2 = 5;
                        const rad2 = Math.hypot(dx, dy);
                        const step2 = (2 * Math.PI) / sides2;
                        for (let i = 0; i < sides2; i++) {
                            const ang = i * step2 - Math.PI / 2;
                            const px = startPos.x + rad2 * Math.cos(ang);
                            const py = startPos.y + rad2 * Math.sin(ang);
                            i === 0 ? ctx2.moveTo(px, py) : ctx2.lineTo(px, py);
                        }
                        ctx2.closePath();
                        ctx2.stroke();
                        break;
                }
            };

            const up = e => {
                if (!isDrawing) return;
                const { x, y } = getPos(e);
                shapesRef.current.push({
                    tool: activeTool,
                    x0: startPos.x,
                    y0: startPos.y,
                    x1: x,
                    y1: y,
                    lineColor,
                    fillColor,
                    strokeWidth,
                    // record whether we held Enter to constrain to circle
                    isCircle: activeTool === "circle" && enterDown,
                    selected: false
                });
                redrawAll();
                ctx2.clearRect(0, 0, c2.width, c2.height);
                setIsDrawing(false);
            };

            const onKeyDown = e => {
                if (e.ctrlKey && e.key === "z" && shapesRef.current.length) {
                    redoRef.current.push(shapesRef.current.pop());
                    redrawAll();
                } else if (
                    e.ctrlKey &&
                    e.shiftKey &&
                    e.key.toLowerCase() === "z" &&
                    redoRef.current.length
                ) {
                    shapesRef.current.push(redoRef.current.pop());
                    redrawAll();
                }
                if (e.key === "Enter") setEnterDown(true);
            };
            const onKeyUp = e => {
                if (e.key === "Enter") setEnterDown(false);
            };

            c2.addEventListener("mousedown", down);
            c2.addEventListener("mousemove", move);
            c2.addEventListener("mouseup", up);
            window.addEventListener("keydown", onKeyDown);
            window.addEventListener("keydown", onKeyUp);
            return () => {
                c2.removeEventListener("mousedown", down);
                c2.removeEventListener("mousemove", move);
                c2.removeEventListener("mouseup", up);
                window.removeEventListener("keydown", onKeyDown);
                window.removeEventListener("keydown", onKeyUp);
            };
        }, [activeTool, isDrawing, startPos, lineColor, fillColor, strokeWidth, enterDown]);

        return (
            <div ref={containerRef} className="canvas-container">
                <canvas ref={c1Ref} />
                <canvas ref={c2Ref} style={{ backgroundColor: "transparent" }} />
            </div>
        );
    }
);

export default Canvas;
